INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES
('afraid', '/əˈfreɪd/', 'Tính từ', 'Adjective', 'Sợ hãi', 'Cảm thấy lo lắng hoặc không an toàn.', '-', 'She was afraid of the dark.', 'Cô ấy sợ bóng tối.'),
('agree', '/əˈɡriː/', 'Động từ', 'Verb', 'Đồng ý', 'Có cùng ý kiến với ai đó.', 'agree - agreed - agreed', 'I agree with your opinion.', 'Tôi đồng ý với ý kiến của bạn.'),
('angry', '/ˈæŋɡri/', 'Tính từ', 'Adjective', 'Tức giận', 'Cảm thấy không hài lòng và có thể phản ứng tiêu cực.', '-', 'He was angry about the mistake.', 'Anh ấy tức giận về sai lầm.'),
('arrive', '/əˈraɪv/', 'Động từ', 'Verb', 'Đến', 'Đến một địa điểm nào đó sau một khoảng thời gian di chuyển.', 'arrive - arrived - arrived', 'The train will arrive at noon.', 'Tàu sẽ đến vào buổi trưa.'),
('attack', '/əˈtæk/', 'Động từ', 'Verb', 'Tấn công', 'Hành động gây hại hoặc làm tổn thương ai đó hoặc cái gì đó.', 'attack - attacked - attacked', 'They plan to attack the enemy base.', 'Họ dự định tấn công căn cứ của kẻ thù.'),
('bottom', '/ˈbɒtəm/', 'Danh từ', 'Noun', 'Đáy', 'Phần thấp nhất hoặc sâu nhất của cái gì đó.', '-', 'The bottom of the sea is very dark.', 'Đáy biển rất tối.'),
('clever', '/ˈklɛvər/', 'Tính từ', 'Adjective', 'Thông minh', 'Có khả năng hiểu biết hoặc học hỏi nhanh chóng.', '-', 'She is a clever student.', 'Cô ấy là một học sinh thông minh.'),
('cruel', '/ˈkruːəl/', 'Tính từ', 'Adjective', 'Tàn nhẫn', 'Hành xử một cách độc ác, không thương xót.', '-', 'It was cruel to treat her that way.', 'Thật tàn nhẫn khi đối xử với cô ấy như vậy.'),
('finally', '/ˈfaɪnəli/', 'Trạng từ', 'Adverb', 'Cuối cùng', 'Sau một thời gian dài hoặc nhiều sự kiện khác.', '-', 'Finally, they reached their destination.', 'Cuối cùng, họ đã đến đích của mình.'),
('hide', '/haɪd/', 'Động từ', 'Verb', 'Giấu', 'Đặt thứ gì đó ở nơi mà người khác không thấy.', 'hide - hid - hidden', 'I like to hide surprises.', 'Tôi thích giấu các món quà bất ngờ.'),
('hunt', '/hʌnt/', 'Động từ', 'Verb', 'Săn', 'Theo đuổi và bắt hoặc giết thú vật.', 'hunt - hunted - hunted', 'They hunt deer in the forest.', 'Họ săn hươu trong rừng.'),
('lot', '/lɒt/', 'Danh từ', 'Noun', 'Nhiều', 'Số lượng lớn hoặc một số lượng không xác định.', '-', 'I have a lot of work to do.', 'Tôi có rất nhiều việc phải làm.'),
('middle', '/ˈmɪdl/', 'Danh từ', 'Noun', 'Giữa', 'Vị trí chính giữa hoặc trung tâm.', '-', 'The middle of the road is the safest place.', 'Giữa đường là nơi an toàn nhất.'),
('moment', '/ˈmoʊmənt/', 'Danh từ', 'Noun', 'Khoảnh khắc', 'Thời gian ngắn, thường chỉ một sự kiện đặc biệt.', '-', 'Wait for a moment.', 'Chờ một chút.'),
('pleased', '/pliːzd/', 'Tính từ', 'Adjective', 'Hài lòng', 'Cảm thấy vui vì điều gì đó.', '-', 'I am pleased with the results.', 'Tôi hài lòng với kết quả.'),
('promise', '/ˈprɒmɪs/', 'Động từ', 'Verb', 'Hứa', 'Cam kết rằng sẽ làm hoặc không làm điều gì.', 'promise - promised - promised', 'I promise to help you.', 'Tôi hứa sẽ giúp bạn.'),
('reply', '/rɪˈplaɪ/', 'Động từ', 'Verb', 'Trả lời', 'Đưa ra phản hồi cho một câu hỏi hoặc yêu cầu.', 'reply - replied - replied', 'Please reply to my email.', 'Vui lòng trả lời email của tôi.'),
('safe', '/seɪf/', 'Tính từ', 'Adjective', 'An toàn', 'Không có nguy hiểm hoặc rủi ro.', '-', 'Make sure you are safe.', 'Hãy chắc chắn rằng bạn ở trong tình trạng an toàn.'),
('trick', '/trɪk/', 'Danh từ', 'Noun', 'Mẹo', 'Một hành động gây ấn tượng hoặc lừa gạt người khác.', '-', 'It was just a trick.', 'Đó chỉ là một mẹo thôi.'),
('well', '/wɛl/', 'Trạng từ', 'Adverb', 'Tốt', 'Trong trạng thái tốt, không có vấn đề.', '-', 'She sings well.', 'Cô ấy hát rất hay.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('adventure', '/ədˈvɛn.tʃər/', 'Danh từ', 'Noun', 'Cuộc phiêu lưu', 'Một trải nghiệm thú vị hoặc không quen thuộc thường liên quan đến hành trình.', '-', 'They went on a great adventure.', 'Họ đã có một cuộc phiêu lưu tuyệt vời.'),  
('approach', '/əˈproʊtʃ/', 'Động từ', 'Verb', 'Tiếp cận', 'Di chuyển gần lại hoặc đến gần ai đó hoặc cái gì đó.', 'approach - approached - approached', 'We need to approach this problem carefully.', 'Chúng ta cần tiếp cận vấn đề này một cách cẩn thận.'),  
('carefully', '/ˈkeər.fəl.i/', 'Trạng từ', 'Adverb', 'Cẩn thận', 'Thực hiện một việc gì đó với sự chú ý để tránh sai lầm.', '-', 'She carefully placed the vase on the shelf.', 'Cô ấy đã đặt bình hoa lên kệ một cách cẩn thận.'),  
('chemical', '/ˈkɛm.ɪ.kəl/', 'Danh từ', 'Noun', 'Hóa chất', 'Một chất có thể là nguyên tố hoặc hợp chất, thường được sử dụng trong khoa học.', '-', 'The chemical reaction was fascinating.', 'Phản ứng hóa học thật thú vị.'),  
('create', '/kriˈeɪt/', 'Động từ', 'Verb', 'Tạo ra', 'Đưa vào thực tế một cái gì đó mới.', 'create - created - created', 'They want to create a new product.', 'Họ muốn tạo ra một sản phẩm mới.'),  
('evil', '/ˈiː.vəl/', 'Tính từ', 'Adjective', 'Ác độc', 'Có thiện cảm xấu, gây hại hoặc đau khổ.', '-', 'He has an evil plan.', 'Anh ấy có một kế hoạch ác độc.'),  
('experiment', '/ɪkˈspɛr.ɪ.mənt/', 'Danh từ', 'Noun', 'Thí nghiệm', 'Một thử nghiệm để khám phá hoặc chứng minh một lý thuyết.', '-', 'The experiment yielded interesting results.', 'Thí nghiệm đã cho kết quả thú vị.'),  
('kill', '/kɪl/', 'Động từ', 'Verb', 'Giết', 'Đưa một sinh vật đến cái chết.', 'kill - killed - killed', 'He didn’t mean to kill the plant.', 'Anh ấy không có ý định giết chết cây.'),  
('laboratory', '/ləˈbɒr.ə.tɔːr.i/', 'Danh từ', 'Noun', 'Phòng thí nghiệm', 'Nơi tiến hành nghiên cứu và thí nghiệm khoa học.', '-', 'The laboratory was well-equipped.', 'Phòng thí nghiệm được trang bị tốt.'),  
('laugh', '/læf/', 'Động từ', 'Verb', 'Cười', 'Phản ứng vui vẻ thể hiện qua âm thanh.', 'laugh - laughed - laughed', 'They laughed at the funny joke.', 'Họ đã cười trước cái đùa vui.'),  
('loud', '/laʊd/', 'Tính từ', 'Adjective', 'To', 'Có âm thanh lớn hoặc dễ nghe.', '-', 'The music was too loud.', 'Âm nhạc quá to.'),  
('nervous', '/ˈnɜr.vəs/', 'Tính từ', 'Adjective', 'Lo lắng', 'Cảm thấy hồi hộp hoặc căng thẳng.', '-', 'She felt nervous before the presentation.', 'Cô ấy cảm thấy lo lắng trước buổi thuyết trình.'),  
('noise', '/nɔɪz/', 'Danh từ', 'Noun', 'Tiếng ồn', 'Âm thanh khó chịu hoặc không cần thiết.', '-', 'The noise outside was distracting.', 'Tiếng ồn bên ngoài khiến tôi phân tâm.'),  
('project', '/ˈprɑdʒɛkt/', 'Danh từ', 'Noun', 'Dự án', 'Một nỗ lực tổ chức nhằm đạt được một mục tiêu cụ thể.', '-', 'The project was completed ahead of schedule.', 'Dự án đã hoàn thành trước tiến độ.'),  
('scare', '/skɛr/', 'Động từ', 'Verb', 'Làm sợ hãi', 'Gây ra cảm giác sợ hãi cho ai đó.', 'scare - scared - scared', 'The movie didn’t scare me at all.', 'Bộ phim không làm tôi sợ chút nào.'),  
('secret', '/ˈsiː.krɪt/', 'Danh từ', 'Noun', 'Bí mật', 'Thông tin mà không ai biết hoặc không muốn tiết lộ.', '-', 'They have a secret to keep.', 'Họ có một bí mật cần giữ.'),  
('shout', '/ʃaʊt/', 'Động từ', 'Verb', 'Hét', 'Nói rất to, thường để thể hiện cảm xúc.', 'shout - shouted - shouted', 'He shouted for help.', 'Anh ấy đã hét lên cầu cứu.'),  
('smell', '/smɛl/', 'Động từ', 'Verb', 'Ngửi', 'Cảm nhận mùi hương bằng mũi.', 'smell - smelled - smelled', 'I can smell the flowers.', 'Tôi có thể ngửi thấy mùi hoa.'),  
('terrible', '/ˈtɛr.ə.bl̩/', 'Tính từ', 'Adjective', 'Kinh khủng', 'Cực kỳ tồi tệ hoặc không hài lòng.', '-', 'The service was terrible.', 'Dịch vụ thật kinh khủng.'),  
('worse', '/wɜrs/', 'Tính từ', 'Adjective', 'Tệ hơn', 'Tình trạng tồi tệ hơn một điều gì đó khác.', '-', 'The situation couldn’t get any worse.', 'Tình hình không thể tồi tệ hơn nữa.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('alien', '/ˈeɪ.li.ən/', 'Danh từ', 'Noun', 'Người ngoài hành tinh', 'Sinh vật giả tưởng từ một hành tinh khác.', '-', 'The alien came from another planet.', 'Người ngoài hành tinh đến từ một hành tinh khác.'),  
('among', '/əˈmʌŋ/', 'Giới từ', 'Preposition', 'Trong số', 'Trong một nhóm hoặc tập hợp những thứ.', '-', 'He was sitting among his friends.', 'Anh ấy đang ngồi trong số bạn bè của mình.'),  
('chart', '/tʃɑrt/', 'Danh từ', 'Noun', 'Biểu đồ', 'Một bản đồ hoặc đồ thị hiển thị thông tin.', '-', 'The chart shows the sales over the year.', 'Biểu đồ cho thấy doanh số bán hàng trong năm.'),  
('cloud', '/klaʊd/', 'Danh từ', 'Noun', 'Đám mây', 'Khối khí ẩm trong bầu khí quyển.', '-', 'The sky is full of clouds.', 'Bầu trời đầy mây.'),  
('comprehend', '/ˌkɒmprɪˈhɛnd/', 'Động từ', 'Verb', 'Hiểu', 'Có khả năng nắm bắt nghĩa của điều gì.', 'comprehend - comprehended - comprehended', 'It is hard to comprehend the meaning of this text.', 'Thật khó để hiểu nghĩa của đoạn văn này.'),  
('describe', '/dɪˈskraɪb/', 'Động từ', 'Verb', 'Miêu tả', 'Nói chi tiết về một người, vật hoặc sự việc.', 'describe - described - described', 'Can you describe what happened?', 'Bạn có thể miêu tả điều gì đã xảy ra không?'),  
('ever', '/ˈɛvər/', 'Trạng từ', 'Adverb', 'Từng', 'Tại một thời điểm nào đó trong quá khứ hoặc tương lai.', '-', 'Have you ever been to Paris?', 'Bạn đã từng đến Paris chưa?'),  
('fail', '/feɪl/', 'Động từ', 'Verb', 'Thất bại', 'Không đạt được điều gì mong muốn.', 'fail - failed - failed', 'I hope I won’t fail the exam.', 'Tôi hy vọng tôi sẽ không thi trượt.'),  
('friendly', '/ˈfrɛnd.li/', 'Tính từ', 'Adjective', 'Thân thiện', 'Có tính cách tốt và dễ gần.', '-', 'She has a friendly smile.', 'Cô ấy có nụ cười thân thiện.'),  
('grade', '/ɡreɪd/', 'Danh từ', 'Noun', 'Điểm', 'Số hiệu hoặc chỉ số cho thấy mức độ thành công trong học tập.', '-', 'He received a good grade on his test.', 'Anh ấy nhận được điểm tốt trong bài kiểm tra.'),  
('instead', '/ɪnˈstɛd/', 'Phó từ', 'Adverb', 'Thay vào đó', 'Chọn một lựa chọn khác thay vì cái khác.', '-', 'I decided to stay home instead.', 'Tôi quyết định ở nhà thay vì ra ngoài.'),  
('library', '/ˈlaɪ.brər.i/', 'Danh từ', 'Noun', 'Thư viện', 'Nơi lưu giữ sách và tài liệu cho việc đọc và nghiên cứu.', '-', 'The library has many resources.', 'Thư viện có nhiều tài liệu hữu ích.'),  
('planet', '/ˈplæn.ɪt/', 'Danh từ', 'Noun', 'Hành tinh', 'Một nghành thiên thể lớn quay xung quanh một ngôi sao.', '-', 'Earth is the third planet from the sun.', 'Trái đất là hành tinh thứ ba từ mặt trời.'),  
('report', '/rɪˈpɔrt/', 'Danh từ', 'Noun', 'Báo cáo', 'Tài liệu tóm tắt thông tin về một chủ đề cụ thể.', '-', 'Please submit your report by Friday.', 'Vui lòng nộp báo cáo của bạn trước thứ Sáu.'),  
('several', '/ˈsɛv.ər.əl/', 'Tính từ', 'Adjective', 'Một số', 'Nhiều hơn hai nhưng không xác định rõ ràng.', '-', 'Several people attended the meeting.', 'Một số người đã tham dự cuộc họp.'),  
('solve', '/sɒlv/', 'Động từ', 'Verb', 'Giải quyết', 'Tìm ra giải pháp cho một vấn đề.', 'solve - solved - solved', 'We need to solve this problem quickly.', 'Chúng ta cần giải quyết vấn đề này một cách nhanh chóng.'),  
('suddenly', '/ˈsʌd.ən.li/', 'Trạng từ', 'Adverb', 'Đột ngột', 'Xảy ra nhanh chóng mà không có cảnh báo.', '-', 'Suddenly, it started to rain.', 'Đột ngột, trời bắt đầu mưa.'),  
('suppose', '/səˈpoʊz/', 'Động từ', 'Verb', 'Giả định', 'Tin rằng một điều gì đó có thể là đúng mà không có chứng minh.', 'suppose - supposed - supposed', 'I suppose we should leave now.', 'Tôi nghĩ rằng chúng ta nên rời đi bây giờ.'),  
('universe', '/ˈjuː.nɪ.vɜːrs/', 'Danh từ', 'Noun', 'Vũ trụ', 'Tất cả các ngôi sao, hành tinh và không gian tồn tại.', '-', 'The universe is vast and mysterious.', 'Vũ trụ thì rộng lớn và đầy bí ẩn.'),  
('view', '/vjuː/', 'Danh từ', 'Noun', 'Quan điểm', 'Cách nhìn nhận hoặc hiểu biết về một điều gì đó.', '-', 'She has a beautiful view from her window.', 'Cô ấy có một cảnh quan đẹp từ cửa sổ của mình.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('appropriate', '/əˈproʊ.pri.ət/', 'Tính từ', 'Adjective', 'Phù hợp', 'Thích hợp với hoàn cảnh hoặc mục đích.', '-', 'Is this behavior appropriate for the situation?', 'Hành vi này có phù hợp với tình huống không?'),  
('avoid', '/əˈvɔɪd/', 'Động từ', 'Verb', 'Tránh', 'Không để xảy ra hoặc không tham gia vào một điều gì đó.', 'avoid - avoided - avoided', 'Try to avoid making the same mistake.', 'Cố gắng tránh làm lại sai lầm như vậy.'),  
('behave', '/bɪˈheɪv/', 'Động từ', 'Verb', 'Hành xử', 'Hành động hoặc cư xử theo cách nhất định.', 'behave - behaved - behaved', 'The children need to learn how to behave in class.', 'Trẻ em cần học cách hành xử trong lớp.'),  
('calm', '/kɑːm/', 'Tính từ', 'Adjective', 'Điềm tĩnh', 'Không lo lắng hoặc nổi giận; bình tĩnh.', '-', 'She remained calm during the emergency.', 'Cô ấy vẫn điềm tĩnh trong tình huống khẩn cấp.'),  
('concern', '/kənˈsɜrn/', 'Danh từ', 'Noun', 'Mối quan tâm', 'Điều gì đó làm bạn lo lắng hoặc chú ý.', '-', 'His health is a major concern for the family.', 'Sức khỏe của anh ấy là một mối quan tâm lớn cho gia đình.'),  
('content', '/ˈkɒn.tent/', 'Danh từ', 'Noun', 'Nội dung', 'Thông tin hoặc ý tưởng được đưa ra trong một tài liệu.', '-', 'The content of the book is very interesting.', 'Nội dung của cuốn sách rất thú vị.'),  
('expect', '/ɪkˈspɛkt/', 'Động từ', 'Verb', 'Mong đợi', 'Tin rằng một điều gì đó sẽ xảy ra trong tương lai.', 'expect - expected - expected', 'I expect to see you at the party.', 'Tôi mong đợi sẽ gặp bạn tại bữa tiệc.'),  
('frequently', '/ˈfriː.kwənt.li/', 'Trạng từ', 'Adverb', 'Thường xuyên', 'Xảy ra nhiều lần trong một khoảng thời gian nhất định.', '-', 'He travels frequently for work.', 'Anh ấy đi công tác thường xuyên.'),  
('habit', '/ˈhæb.ɪt/', 'Danh từ', 'Noun', 'Thói quen', 'Một hành động được thực hiện thường xuyên đến mức trở thành tự động.', '-', 'She has a habit of reading before bed.', 'Cô ấy có thói quen đọc sách trước khi đi ngủ.'),  
('instruct', '/ɪnˈstrʌkt/', 'Động từ', 'Verb', 'Hướng dẫn', 'Cung cấp thông tin hoặc chỉ dẫn để ai đó thực hiện điều gì.', 'instruct - instructed - instructed', 'The teacher will instruct the students on how to complete the assignment.', 'Giáo viên sẽ hướng dẫn học sinh cách hoàn thành bài tập.'),  
('issue', '/ˈɪʃ.uː/', 'Danh từ', 'Noun', 'Vấn đề', 'Một chủ đề hoặc tình huống mà mọi người quan tâm hoặc tranh cãi.', '-', 'This is a serious issue that needs to be addressed.', 'Đây là một vấn đề nghiêm trọng cần được giải quyết.'),  
('none', '/nʌn/', 'Đại từ', 'Pronoun', 'Không ai', 'Không có cái gì hoặc không có ai.', '-', 'None of the options seemed right.', 'Không có lựa chọn nào có vẻ đúng.'),  
('patient', '/ˈpeɪ.ʃənt/', 'Tính từ', 'Adjective', 'Kiên nhẫn', 'Có khả năng chịu đựng trong tình huống khó khăn mà không trở nên tức giận.', '-', 'He is very patient with his students.', 'Anh ấy rất kiên nhẫn với học sinh của mình.'),  
('positive', '/ˈpɒz.ɪ.tɪv/', 'Tính từ', 'Adjective', 'Tích cực', 'Có ảnh hưởng tốt, vui vẻ hoặc mang lại hứa hẹn.', '-', 'She always has a positive attitude.', 'Cô ấy luôn có thái độ tích cực.'),  
('punish', '/ˈpʌn.ɪʃ/', 'Động từ', 'Verb', 'Phạt', 'Đưa ra hình phạt cho một hành động sai trái.', 'punish - punished - punished', 'They decided to punish him for his mistakes.', 'Họ quyết định phạt anh ấy vì những sai lầm của mình.'),  
('represent', '/ˌrɛp.rɪˈzɛnt/', 'Động từ', 'Verb', 'Đại diện', 'Được coi là biểu tượng cho một cái gì đó.', 'represent - represented - represented', 'This painting represents our culture.', 'Bức tranh này đại diện cho văn hóa của chúng ta.'),  
('shake', '/ʃeɪk/', 'Động từ', 'Verb', 'Lắc', 'Di chuyển qua lại hoặc lên xuống.', 'shake - shook - shaken', 'She will shake your hand after the meeting.', 'Cô ấy sẽ bắt tay bạn sau cuộc họp.'),  
('spread', '/sprɛd/', 'Động từ', 'Verb', 'Mở rộng', 'Lan tỏa ra một khu vực rộng lớn hơn.', 'spread - spread - spread', 'The news will spread quickly.', 'Tin tức sẽ lan truyền nhanh chóng.'),  
('stroll', '/strəʊl/', 'Động từ', 'Verb', 'Đi dạo', 'Đi bộ một cách thoải mái hoặc thư giãn.', 'stroll - strolled - strolled', 'They like to stroll in the park.', 'Họ thích đi dạo trong công viên.'),  
('village', '/ˈvɪl.ɪdʒ/', 'Danh từ', 'Noun', 'Làng', 'Một cộng đồng nhỏ thường nằm ở nông thôn.', '-', 'She grew up in a small village.', 'Cô ấy lớn lên trong một ngôi làng nhỏ.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('aware', '/əˈwɛər/', 'Tính từ', 'Adjective', 'Nhận thức', 'Có kiến thức hoặc hiểu biết về điều gì.', '-', 'She was not aware of the situation.', 'Cô ấy không nhận thức được tình huống này.'),  
('badly', '/ˈbæd.li/', 'Trạng từ', 'Adverb', 'Tồi tệ', 'Theo cách không tốt hoặc không đúng.', '-', 'He plays the guitar badly.', 'Anh ấy chơi guitar rất tồi.'),  
('belong', '/bɪˈlɔːŋ/', 'Động từ', 'Verb', 'Thuộc về', 'Có liên quan hoặc thuộc về một nhóm hoặc nơi nào đó.', 'belong - belonged - belonged', 'I feel I belong here.', 'Tôi cảm thấy mình thuộc về đây.'),  
('continue', '/kənˈtɪn.juː/', 'Động từ', 'Verb', 'Tiếp tục', 'Tiếp tục một hành động hoặc quá trình mà đã bắt đầu.', 'continue - continued - continued', 'Please continue with your story.', 'Vui lòng tiếp tục câu chuyện của bạn.'),  
('error', '/ˈɛr.ər/', 'Danh từ', 'Noun', 'Lỗi', 'Một sai sót hoặc nhầm lẫn.', '-', 'There is an error in your calculations.', 'Có một lỗi trong phép tính của bạn.'),  
('experience', '/ɪkˈspɪə.ri.əns/', 'Danh từ', 'Noun', 'Trải nghiệm', 'Điều gì đó đã xảy ra với một người mà họ đã cảm nhận hoặc học hỏi.', '-', 'I gained a lot of experience from this job.', 'Tôi đã tích lũy được nhiều kinh nghiệm từ công việc này.'),  
('field', '/fiːld/', 'Danh từ', 'Noun', 'Lĩnh vực', 'Một khu vực ảnh hưởng đến một ngành nghề hoặc hoạt động riêng.', '-', 'She works in the field of education.', 'Cô ấy làm việc trong lĩnh vực giáo dục.'),  
('hurt', '/hɜːrt/', 'Động từ', 'Verb', 'Làm đau', 'Gây ra đau đớn cho ai đó hoặc thứ gì đó.', 'hurt - hurt - hurt', 'He didn’t mean to hurt her feelings.', 'Anh ấy không có ý định làm tổn thương cảm xúc của cô ấy.'),  
('judgment', '/ˈdʒʌdʒ.mənt/', 'Danh từ', 'Noun', 'Phán quyết', 'Quan điểm hoặc đánh giá mà ai đó đưa ra về một tình huống hoặc người khác.', '-', 'Her judgment was impaired by emotions.', 'Phán quyết của cô ấy bị ảnh hưởng bởi cảm xúc.'),  
('likely', '/ˈlaɪ.kli/', 'Tính từ', 'Adjective', 'Có thể', 'Có khả năng xảy ra hoặc đúng.', '-', 'It is likely to rain today.', 'Hôm nay có khả năng sẽ mưa.'),  
('normal', '/ˈnɔːr.məl/', 'Tính từ', 'Adjective', 'Bình thường', 'Sự kiện hoặc tình trạng xảy ra thường xuyên hoặc theo quy tắc.', '-', 'This reaction is normal.', 'Phản ứng này là bình thường.'),  
('rare', '/rɛər/', 'Tính từ', 'Adjective', 'Hiếm', 'Không phổ biến hoặc xảy ra không thường xuyên.', '-', 'It is rare to see such wildlife.', 'Rất hiếm khi thấy động vật hoang dã như vậy.'),  
('relax', '/rɪˈlæks/', 'Động từ', 'Verb', 'Thư giãn', 'Ngừng lo lắng hoặc căng thẳng.', 'relax - relaxed - relaxed', 'You need to relax a little.', 'Bạn cần thư giãn một chút.'),  
('request', '/rɪˈkwɛst/', 'Động từ', 'Verb', 'Yêu cầu', 'Hỏi ai đó làm điều gì đó cho bạn.', 'request - requested - requested', 'I would like to request a meeting.', 'Tôi muốn yêu cầu một cuộc họp.'),  
('reside', '/rɪˈzaɪd/', 'Động từ', 'Verb', 'Cư trú', 'Sống hoặc cư ngụ ở một nơi nào đó.', 'reside - resided - resided', 'They reside in a small town.', 'Họ cư trú ở một thị trấn nhỏ.'),  
('result', '/rɪˈzʌlt/', 'Danh từ', 'Noun', 'Kết quả', 'Điều xảy ra hoặc thu được từ một tình huống hoặc hành động.', '-', 'The result was better than expected.', 'Kết quả tốt hơn mong đợi.'),  
('roll', '/roʊl/', 'Động từ', 'Verb', 'Lăn', 'Di chuyển một vật tròn bằng cách lăn nó.', 'roll - rolled - rolled', 'They will roll the ball down the hill.', 'Họ sẽ lăn bóng xuống đồi.'),  
('since', '/sɪns/', 'Giới từ', 'Preposition', 'Kể từ', 'Từ một thời điểm cụ thể trở đi.', '-', 'He has lived here since 2010.', 'Anh ấy đã sống ở đây kể từ năm 2010.'),  
('visible', '/ˈvɪz.ə.bəl/', 'Tính từ', 'Adjective', 'Có thể nhìn thấy', 'Có khả năng được nhìn thấy hoặc nhận thấy.', '-', 'The stars are visible tonight.', 'Các ngôi sao có thể nhìn thấy được tối nay.'),  
('wild', '/waɪld/', 'Tính từ', 'Adjective', 'Hoang dã', 'Sống hoặc phát triển tự nhiên mà không bị con người kiểm soát.', '-', 'The wild animals roam freely in the forest.', 'Động vật hoang dã tự do lang thang trong rừng.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('advantage', '/ədˈvæn.tɪdʒ/', 'Danh từ', 'Noun', 'Lợi thế', 'Điều gì đó có lợi thế hoặc mang lại lợi ích.', '-', 'Having a car gives you a significant advantage.', 'Có xe hơi mang lại cho bạn một lợi thế lớn.'),  
('cause', '/kɔːz/', 'Danh từ', 'Noun', 'Nguyên nhân', 'Điều gì làm cho một sự kiện xảy ra.', '-', 'The cause of the problem is still unknown.', 'Nguyên nhân của vấn đề vẫn chưa được biết đến.'),  
('choice', '/tʃɔɪs/', 'Danh từ', 'Noun', 'Lựa chọn', 'Sự quyết định giữa hai hoặc nhiều khả năng hoặc sự lựa chọn.', '-', 'There are many choices on the menu.', 'Có nhiều lựa chọn trên thực đơn.'),  
('community', '/kəˈmjuː.nɪ.ti/', 'Danh từ', 'Noun', 'Cộng đồng', 'Nhóm người sống trong một khu vực nhất định hoặc chia sẻ một cái gì đó chung.', '-', 'The community came together to help those in need.', 'Cộng đồng đã cùng nhau giúp đỡ những người cần.'),  
('dead', '/dɛd/', 'Tính từ', 'Adjective', 'Chết', 'Không còn sự sống; không hoạt động.', '-', 'The plant died because it didn’t get enough water.', 'Cây chết vì không được tưới đủ nước.'),  
('distance', '/ˈdɪs.təns/', 'Danh từ', 'Noun', 'Khoảng cách', 'Sự cách biệt giữa hai điểm hoặc hai vật thể.', '-', 'The distance between the two cities is 200 kilometers.', 'Khoảng cách giữa hai thành phố là 200 cây số.'),  
('escape', '/ɪˈskeɪp/', 'Động từ', 'Verb', 'Tránh', 'Rời khỏi một nơi hoặc một tình huống có thể gây hại.', 'escape - escaped - escaped', 'The prisoners managed to escape from the jail.', 'Các tù nhân đã trốn khỏi nhà tù.'),  
('face', '/feɪs/', 'Động từ', 'Verb', 'Đối mặt', 'Chạm trán với hoặc nhận thức được một điều gì khó khăn.', 'face - faced - faced', 'You must face your fears.', 'Bạn phải đối mặt với nỗi sợ hãi của mình.'),  
('follow', '/ˈfɒl.oʊ/', 'Động từ', 'Verb', 'Theo dõi', 'Đi sau một người hoặc điều gì đó; chấp nhận hoặc tuân theo chỉ dẫn.', 'follow - followed - followed', 'Please follow the instructions carefully.', 'Vui lòng theo dõi hướng dẫn một cách cẩn thận.'),  
('fright', '/fraɪt/', 'Danh từ', 'Noun', 'Sự hoảng sợ', 'Cảm giác sợ hãi hoặc lo lắng đột ngột.', '-', 'He jumped in fright when the dog barked.', 'Anh ấy nhảy lên trong sự hoảng sợ khi con chó sủa.'),  
('ghost', '/ɡoʊst/', 'Danh từ', 'Noun', 'Ma', 'Hồn ma của một người đã chết, thường được cho là có thể hiện hữu.', '-', 'Many people believe in ghosts.', 'Nhiều người tin vào ma.'),  
('individual', '/ˌɪn.dɪˈvɪdʒ.u.əl/', 'Danh từ', 'Noun', 'Cá nhân', 'Người hoặc vật mà không phải là một phần của nhóm lớn hơn.', '-', 'Each individual has the right to express their opinions.', 'Mỗi cá nhân có quyền bày tỏ ý kiến của mình.'),  
('pet', '/pɛt/', 'Danh từ', 'Noun', 'Thú cưng', 'Động vật có người nuôi trong gia đình làm bạn.', '-', 'Dogs and cats are popular pets.', 'Chó và mèo là thú cưng phổ biến.'),  
('reach', '/riːtʃ/', 'Động từ', 'Verb', 'Với tới', 'Đạt được hoặc liên lạc với ai đó hoặc điều gì đó.', 'reach - reached - reached', 'She can reach the top shelf.', 'Cô ấy có thể với tới kệ trên cùng.'),  
('return', '/rɪˈtɜrn/', 'Động từ', 'Verb', 'Trở về', 'Quay lại nơi mà bạn đã đến từ đó.', 'return - returned - returned', 'He will return home tomorrow.', 'Anh ấy sẽ trở về nhà vào ngày mai.'),  
('survive', '/sərˈvaɪv/', 'Động từ', 'Verb', 'Tồn tại', 'Tiếp tục sống hoặc tồn tại, đặc biệt là sau một sự kiện khó khăn.', 'survive - survived - survived', 'They survived the storm.', 'Họ đã sống sót sau cơn bão.'),  
('upset', '/ʌpˈsɛt/', 'Tính từ', 'Adjective', 'Buồn bã', 'Cảm thấy không thoải mái hoặc lo lắng; làm cho ai đó cảm thấy như vậy.', '-', 'She was upset by the news.', 'Cô ấy đã buồn bã vì tin này.'),  
('voice', '/vɔɪs/', 'Danh từ', 'Noun', 'Giọng nói', 'Âm thanh mà con người sử dụng để nói.', '-', 'Her voice is very soothing.', 'Giọng nói của cô ấy rất dịu dàng.'),  
('weather', '/ˈwɛð.ər/', 'Danh từ', 'Noun', 'Thời tiết', 'Tình trạng của không khí tại một nơi và thời điểm xác định, bao gồm nhiệt độ và độ ẩm.', '-', 'The weather is nice today.', 'Thời tiết hôm nay thật đẹp.'),  
('wise', '/waɪz/', 'Tính từ', 'Adjective', 'Khôn ngoan', 'Có khả năng đưa ra quyết định tốt dựa trên hiểu biết và kinh nghiệm.', '-', 'It is wise to save money for the future.', 'Thật khôn ngoan khi tiết kiệm tiền cho tương lai.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('allow', '/əˈlaʊ/', 'Động từ', 'Verb', 'Cho phép', 'Cung cấp sự cho phép để ai đó làm điều gì đó.', 'allow - allowed - allowed', 'Teachers allow students to use calculators.', 'Giáo viên cho phép học sinh sử dụng máy tính cầm tay.'),  
('announce', '/əˈnaʊns/', 'Động từ', 'Verb', 'Thông báo', 'Đưa ra thông tin chính thức hoặc công bố điều gì đó.', 'announce - announced - announced', 'They will announce the winner tomorrow.', 'Họ sẽ thông báo người chiến thắng vào ngày mai.'),  
('beside', '/bɪˈsaɪd/', 'Giới từ', 'Preposition', 'Bên cạnh', 'Ở một bên của ai đó hoặc cái gì đó.', '-', 'She sat beside her friend during the movie.', 'Cô ấy ngồi bên cạnh bạn mình trong suốt bộ phim.'),  
('challenge', '/ˈtʃæl.ɪndʒ/', 'Danh từ', 'Noun', 'Thách thức', 'Một vấn đề hoặc nhiệm vụ khó khăn mà ai đó phải đối mặt.', '-', 'He met the challenge with determination.', 'Anh ấy đã đối mặt với thách thức bằng quyết tâm.'),  
('claim', '/kleɪm/', 'Động từ', 'Verb', 'Khẳng định', 'Nói rằng điều gì đó là đúng, thường không có bằng chứng.', 'claim - claimed - claimed', 'She claimed to have seen a UFO.', 'Cô ấy khẳng định đã nhìn thấy một chiếc UFO.'),  
('condition', '/kənˈdɪʃ.ən/', 'Danh từ', 'Noun', 'Tình trạng', 'Cách mà một cái gì đó tồn tại hoặc được xử lý; các yếu tố giới hạn hoặc quy định.', '-', 'The car is in excellent condition.', 'Xe ô tô đang ở trong tình trạng tuyệt vời.'),  
('contribute', '/kənˈtrɪb.juːt/', 'Động từ', 'Verb', 'Đóng góp', 'Cung cấp một phần vào một cái gì đó lớn hơn.', 'contribute - contributed - contributed', 'Everyone can contribute to the project.', 'Mọi người đều có thể đóng góp cho dự án.'),  
('difference', '/ˈdɪf.ər.əns/', 'Danh từ', 'Noun', 'Sự khác biệt', 'Sự không tương đồng hoặc không giống nhau giữa hai điều gì đó.', '-', 'What is the difference between these two products?', 'Sự khác biệt giữa hai sản phẩm này là gì?'),  
('divide', '/dɪˈvaɪd/', 'Động từ', 'Verb', 'Chia', 'Tách một cái gì đó thành nhiều phần.', 'divide - divided - divided', 'Please divide the cake into equal parts.', 'Vui lòng chia chiếc bánh thành các phần bằng nhau.'),  
('expert', '/ˈɛk.spɜrt/', 'Danh từ', 'Noun', 'Chuyên gia', 'Người có kiến thức, kỹ năng hoặc thành thạo trong một lĩnh vực cụ thể.', '-', 'She is an expert in environmental science.', 'Cô ấy là một chuyên gia về khoa học môi trường.'),  
('famous', '/ˈfeɪ.məs/', 'Tính từ', 'Adjective', 'Nổi tiếng', 'Được biết đến rộng rãi hoặc nổi bật vì điều gì đó.', '-', 'He is a famous author.', 'Ông là một tác giả nổi tiếng.'),  
('force', '/fɔrs/', 'Danh từ', 'Noun', 'Lực', 'Sức mạnh hoặc năng lượng để gây ra một tác động.', '-', 'The force of the wind broke the branches.', 'Lực của gió đã làm gãy các cành cây.'),  
('harm', '/hɑrm/', 'Danh từ', 'Noun', 'Thiệt hại', 'Hành động gây ra tổn thất hoặc đau đớn.', '-', 'They were concerned about the harm to wildlife.', 'Họ lo ngại về sự thiệt hại đối với động vật hoang dã.'),  
('lay', '/leɪ/', 'Động từ', 'Verb', 'Đặt', 'Đặt một cái gì đó xuống một cách thẳng thắn.', 'lay - laid - laid', 'Please lay the book on the table.', 'Vui lòng đặt cuốn sách lên bàn.'),  
('peace', '/piːs/', 'Danh từ', 'Noun', 'Hòa bình', 'Tình trạng không có chiến tranh hoặc bạo lực; sự yên tĩnh bên trong.', '-', 'They want to live in peace.', 'Họ muốn sống trong hòa bình.'),  
('prince', '/prɪns/', 'Danh từ', 'Noun', 'Hoàng tử', 'Con trai của vua hoặc hoàng hậu.', '-', 'The prince will be visiting the village.', 'Hoàng tử sẽ đến thăm ngôi làng.'),  
('protect', '/prəˈtɛkt/', 'Động từ', 'Verb', 'Bảo vệ', 'Đảm bảo an toàn cho ai đó hoặc điều gì đó khỏi nguy hiểm.', 'protect - protected - protected', 'We need to protect the environment.', 'Chúng ta cần bảo vệ môi trường.'),  
('sense', '/sɛns/', 'Danh từ', 'Noun', 'Cảm giác', 'Khả năng cảm nhận một điều gì đó qua các giác quan.', '-', 'She has a strong sense of smell.', 'Cô ấy có khả năng ngửi rất nhạy.'),  
('sudden', '/ˈsʌd.ən/', 'Tính từ', 'Adjective', 'Đột ngột', 'Xảy ra nhanh chóng và không có cảnh báo.', '-', 'There was a sudden change in temperature.', 'Đã có sự thay đổi đột ngột về nhiệt độ.'),  
('therefore', '/ˈðer.fɔr/', 'Phó từ', 'Adverb', 'Do đó', 'Vì lý do đó; cho nên.', '-', 'It was raining; therefore, the event was canceled.', 'Trời đang mưa; do đó, sự kiện đã bị hủy.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
