INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('academy', '/əˈkæd.ə.mi/', 'Danh từ', 'Noun', 'Học viện', 'Một tổ chức giáo dục, thường là một nơi đào tạo hoặc nghiên cứu.', '-', 'She was accepted into the prestigious academy.', 'Cô ấy đã được nhận vào học viện danh giá.'),  
('ancient', '/ˈeɪn.ʃənt/', 'Tính từ', 'Adjective', 'Cổ xưa', 'Rất cũ, từ thời kỳ lịch sử xa xưa.', '-', 'The ancient ruins attract many tourists.', 'Những di tích cổ xưa thu hút nhiều du khách.'),  
('board', '/bɔrd/', 'Danh từ', 'Noun', 'Cái bảng', 'Một mảnh phẳng của vật liệu, thường dùng để viết hoặc dùng trong các hoạt động khác.', '-', 'He wrote the notes on the board.', 'Anh ấy đã viết các ghi chú lên bảng.'),  
