INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('advertise', '/ˈæd.və.taɪz/', 'Động từ', 'Verb', 'Quảng cáo', 'Trình bày hoặc phổ biến thông tin về một sản phẩm hoặc dịch vụ.', 'advertise - advertised - advertised', 'They will advertise the new product next week.', 'Họ sẽ quảng cáo sản phẩm mới vào tuần tới.'),  
