('broadcast', '/ˈbrɔːd.kæst/', 'Động từ', 'Verb', 'Phát sóng', 'Truyền thông tin đến một số lượng lớn người thông qua các phương tiện truyền thông.', 'broadcast - broadcasted - broadcasted', 'The news was broadcast live on television.', 'Tin tức đã được phát sóng trực tiếp trên truyền hình.'),  
('bulletin', '/ˈbʊl.ɪ.tɪn/', 'Danh từ', 'Noun', 'Thông báo', 'Một bản thông cáo hoặc báo cáo ngắn.', '-', 'The organization issued a bulletin about the upcoming event.', 'Tổ chức đã phát hành một thông báo về sự kiện sắp tới.'),  
('bump', '/bʌmp/', 'Động từ', 'Verb', 'Va chạm', 'Tạo ra một cú va chạm với một vật thể khác.', 'bump - bumped - bumped', 'He bumped into a lamppost while walking.', 'Anh ấy đã va vào cột đèn khi đang đi bộ.'),  
('chop', '/tʃɒp/', 'Động từ', 'Verb', 'Chặt', 'Cắt một cái gì đó bằng một công cụ sắc.', 'chop - chopped - chopped', 'She chopped the vegetables for the soup.', 'Cô ấy đã chặt rau cho món súp.'),  
('closet', '/ˈklɒz.ɪt/', 'Danh từ', 'Noun', 'Tủ quần áo', 'Một không gian kín với cửa dùng để chứa quần áo.', '-', 'He keeps his clothes in the closet.', 'Anh ấy giữ quần áo trong tủ quần áo.'),  
('console', '/ˈkɒn.səʊl/', 'Động từ', 'Verb', 'An ủi', 'Giúp ai đó cảm thấy tốt hơn khi họ buồn.', 'console - consoled - consoled', 'She consoled him after his loss.', 'Cô đã an ủi anh sau sự mất mát của anh.'),  
('district', '/ˈdɪs.trɪkt/', 'Danh từ', 'Noun', 'Quận', 'Một khu vực địa lý được xác định rõ ràng.', '-', 'This district is known for its beautiful parks.', 'Quận này nổi tiếng với những công viên đẹp.'),  
('drawer', '/ˈdrɔː.ər/', 'Danh từ', 'Noun', 'Ngăn kéo', 'Một phần của một chiếc tủ hoặc bàn dùng để chứa đồ vật.', '-', 'The socks are in the drawer.', 'Những chiếc tất ở trong ngăn kéo.'),  
('endure', '/ɪnˈdʒʊr/', 'Động từ', 'Verb', 'Chịu đựng', 'Tồn tại qua những khó khăn hoặc đau đớn.', 'endure - endured - endured', 'They had to endure a long wait.', 'Họ đã phải chịu đựng một khoảng chờ đợi dài.'),  
('execute', '/ˈɛk.sɪ.kjuːt/', 'Động từ', 'Verb', 'Thực hiện', 'Tiến hành một hành động hoặc nhiệm vụ.', 'execute - executed - executed', 'The programmer executed the code successfully.', 'Lập trình viên đã thực hiện mã thành công.'),  
('grasp', '/ɡræsp/', 'Động từ', 'Verb', 'Nắm giữ', 'Nắm hoặc hiểu một khái niệm hoặc vật gì đó.', 'grasp - grasped - grasped', 'He grasped the concept quickly.', 'Anh ấy đã nắm bắt khái niệm nhanh chóng.'),  
('rear', '/rɪər/', 'Động từ', 'Verb', 'Nuôi dưỡng', 'Để chăm sóc và nuôi dạy một đứa trẻ hoặc vật nuôi.', 'rear - reared - reared', 'They reared their children with love.', 'Họ đã nuôi dưỡng con cái bằng tình yêu thương.'),  
('senator', '/ˈsɛn.ə.tər/', 'Danh từ', 'Noun', 'Thượng nghị sĩ', 'Một thành viên của cơ quan lập pháp cao nhất.', '-', 'The senator addressed the crowd at the rally.', 'Thượng nghị sĩ đã phát biểu với đám đông tại cuộc biểu tình.'),  
('skull', '/skʌl/', 'Danh từ', 'Noun', 'Xương sọ', 'Phần xương của đầu mà chứa não.', '-', 'The archaeologist found a human skull.', 'Nhà khảo cổ đã tìm thấy một bộ xương sọ người.'),  
('stir', '/stɜːr/', 'Động từ', 'Verb', 'Khuấy', 'Di chuyển một chất lỏng bằng một dụng cụ để trộn đều.', 'stir - stirred - stirred', 'She stirred the soup to mix the ingredients.', 'Cô ấy khuấy súp để trộn đều các nguyên liệu.'),  
('tap', '/tæp/', 'Động từ', 'Verb', 'Gõ nhẹ', 'Gõ nhẹ vào một bề mặt.', 'tap - tapped - tapped', 'He tapped the table to get her attention.', 'Anh ấy đã gõ nhẹ vào bàn để thu hút sự chú ý của cô.'),  
('tremendous', '/trəˈmɛn.dəs/', 'Tính từ', 'Adjective', 'Khổng lồ', 'Rất lớn hoặc ấn tượng.', '-', 'The concert was a tremendous success.', 'Buổi hòa nhạc là một thành công to lớn.'),  
('underneath', '/ʌndərˈniːθ/', 'Giới từ', 'Preposition', 'Dưới', 'Phía dưới một vật gì đó.', '-', 'The cat is hiding underneath the bed.', 'Con mèo đang trốn dưới giường.'),  
('worm', '/wɜːrm/', 'Danh từ', 'Noun', 'Giun', 'Một sinh vật nhỏ, mềm mại, thường sống trong đất.', '-', 'The gardener found a worm in the soil.', 'Người làm vườn đã tìm thấy một con giun trong đất.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('abandon', '/əˈbændən/', 'Động từ', 'Verb', 'Từ bỏ', 'Rời bỏ một nơi hoặc người mà không có ý định quay lại.', 'abandon - abandoned - abandoned', 'They had to abandon their plans due to the weather.', 'Họ đã phải từ bỏ kế hoạch của mình do thời tiết.'),  
('ambitious', '/æmˈbɪʃəs/', 'Tính từ', 'Adjective', 'Tham vọng', 'Có mong muốn đạt được thành công hoặc quyền lực.', '-', 'She has ambitious goals for her career.', 'Cô ấy có những mục tiêu tham vọng cho sự nghiệp của mình.'),  
('bark', '/bɑːrk/', 'Động từ', 'Verb', 'Sủa', 'Phát ra âm thanh giống như tiếng chó.', 'bark - barked - barked', 'The dog barked at the strangers.', 'Con chó đã sủa vào những người lạ.'),  
('bay', '/beɪ/', 'Động từ', 'Verb', 'Dền dứ', 'Hầu như phát ra âm thanh giống như của chó.', 'bay - bayed - bayed', 'The hound bayed at the moon.', 'Chó săn đã dền dứ vào mặt trăng.'),  
('brilliant', '/ˈbrɪljənt/', 'Tính từ', 'Adjective', 'Rực rỡ', 'Ánh sáng sáng chói hoặc rất thông minh.', '-', 'She had a brilliant idea for the project.', 'Cô ấy đã có một ý tưởng rực rỡ cho dự án.'),  
('chin', '/tʃɪn/', 'Danh từ', 'Noun', 'Cằm', 'Phần dưới của mặt, dưới miệng.', '-', 'He rested his chin on his hand.', 'Anh ấy đã đặt cằm lên tay.'),  
('complaint', '/kəmˈpleɪnt/', 'Danh từ', 'Noun', 'Khiếu nại', 'Một lời phàn nàn về một điều gì đó không đúng.', '-', 'She filed a complaint with the management.', 'Cô ấy đã nộp một đơn khiếu nại lên ban quản lý.'),  
('deaf', '/dɛf/', 'Tính từ', 'Adjective', 'Điếc', 'Không thể nghe hoặc khó nghe.', '-', 'He is deaf in one ear.', 'Anh ấy bị điếc ở một tai.'),  
('enthusiastic', '/ɪnˌθjuː.ziˈæstɪk/', 'Tính từ', 'Adjective', 'Nhiệt tình', 'Có sự quan tâm hoặc nhiệt tình mạnh mẽ đối với một điều gì đó.', '-', 'The players were enthusiastic about the game.', 'Các cầu thủ rất nhiệt tình với trận đấu.'),  
('expedition', '/ˌɛk.spəˈdɪʃ.ən/', 'Danh từ', 'Noun', 'Cuộc thám hiểm', 'Một chuyến đi có mục đích khám phá hoặc nghiên cứu.', '-', 'The expedition to the North Pole was successful.', 'Cuộc thám hiểm đến Bắc Cực đã thành công.'),  
('horizon', '/həˈraɪ.zən/', 'Danh từ', 'Noun', 'Chân trời', 'Nơi mà mặt đất gặp bầu trời.', '-', 'The sun set below the horizon.', 'Mặt trời đã lặn dưới chân trời.'),  
('loyal', '/ˈlɔɪ.əl/', 'Tính từ', 'Adjective', 'Trung thành', 'Luôn đứng về phía một người, nhóm, hoặc ý tưởng.', '-', 'He is a loyal friend.', 'Anh ấy là một người bạn trung thành.'),  
('mayor', '/ˈmeɪ.ər/', 'Danh từ', 'Noun', 'Thị trưởng', 'Người đứng đầu chính quyền của một thành phố hoặc thị trấn.', '-', 'The mayor delivered a speech at the ceremony.', 'Thị trưởng đã có một bài phát biểu tại buổi lễ.'),  
('mutual', '/ˈmjuː.tʃu.əl/', 'Tính từ', 'Adjective', 'Tương hỗ', 'Cả hai bên đều có lợi hoặc chịu trách nhiệm.' , '-', 'They have mutual respect for each other.', 'Họ có sự tôn trọng lẫn nhau.'),  
('overweight', '/ˈoʊ.vər.weɪt/', 'Tính từ', 'Adjective', 'Thừa cân', 'Nặng hơn mức bình thường hoặc lý tưởng.', '-', 'He was advised to lose weight because he was overweight.', 'Anh ấy đã được khuyên giảm cân vì thừa cân.'),  
('refuge', '/ˈrɛf.juːdʒ/', 'Danh từ', 'Noun', 'Nơi trú ẩn', 'Một nơi an toàn để tìm bảo vệ.', '-', 'They sought refuge during the storm.', 'Họ đã tìm nơi trú ẩn trong cơn bão.'),  
('restore', '/rɪˈstɔːr/', 'Động từ', 'Verb', 'Khôi phục', 'Đem trả lại điều gì về trạng thái ban đầu.', 'restore - restored - restored', 'The workers restored the old building.', 'Các công nhân đã khôi phục tòa nhà cũ.'),  
('rub', '/rʌb/', 'Động từ', 'Verb', 'Chà xát', 'Di chuyển tay hoặc một vật trên bề mặt của một vật khác.', 'rub - rubbed - rubbed', 'He rubbed his eyes in disbelief.', 'Anh ấy đã chà xát mắt trong sự không thể tin được.'),  
('senses', '/ˈsɛn.sɪz/', 'Danh từ', 'Noun', 'Giác quan', 'Những khả năng cảm nhận môi trường như thấy, nghe, nếm, ngửi, và chạm.', '-', 'The blind man relied on his other senses.', 'Người mù phụ thuộc vào các giác quan khác của mình.'),  
('veterinarian', '/ˌvɛt.ər.ɪˈneə.ri.ən/', 'Danh từ', 'Noun', 'Bác sĩ thú y', 'Một người có chuyên môn trong chăm sóc sức khỏe cho động vật.', '-', 'The veterinarian treated the injured dog.', 'Bác sĩ thú y đã điều trị cho con chó bị thương.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('anniversary', '/ˌæn.ɪˈvɜːr.sə.ri/', 'Danh từ', 'Noun', 'Ngày kỷ niệm', 'Ngày mà một sự kiện nào đó xảy ra và được nhớ đến hàng năm.', '-', 'They celebrated their wedding anniversary with a special dinner.', 'Họ đã kỷ niệm ngày cưới của mình bằng một bữa ăn đặc biệt.'),  
('arithmetic', '/əˈrɪθ.mə.tɪk/', 'Danh từ', 'Noun', 'Số học', 'Phép tính cơ bản bao gồm cộng, trừ, nhân và chia.', '-', 'Arithmetic is a fundamental part of mathematics.', 'Số học là một phần cơ bản của toán học.'),  
('ashamed', '/əˈʃeɪmd/', 'Tính từ', 'Adjective', 'Xấu hổ', 'Cảm thấy không thoải mái vì điều gì đó mà mình đã làm.', '-', 'She felt ashamed of her behavior.', 'Cô ấy cảm thấy xấu hổ về hành vi của mình.'),  
('burst', '/bɜːrst/', 'Động từ', 'Verb', 'Bùng nổ', 'Vỡ ra một cách bất ngờ.', 'burst - burst - burst', 'The balloon burst when it hit the sharp object.', 'Bóng bay đã bùng nổ khi va vào vật sắc nhọn.'),  
('carpenter', '/ˈkɑːr.pɪn.tər/', 'Danh từ', 'Noun', 'Thợ mộc', 'Người làm việc với gỗ để tạo ra các cấu trúc và đồ vật.', '-', 'The carpenter built a beautiful wooden table.', 'Người thợ mộc đã xây dựng một chiếc bàn gỗ đẹp.'),  
('coal', '/koʊl/', 'Danh từ', 'Noun', 'Than', 'Một loại nhiên liệu hóa thạch, chủ yếu được sử dụng để sản xuất năng lượng.', '-', 'They used coal to heat the house in winter.', 'Họ đã sử dụng than để sưởi ấm ngôi nhà vào mùa đông.'),  
('couch', '/kaʊtʃ/', 'Danh từ', 'Noun', 'Sofa', 'Một chiếc ghế dài dùng để ngồi thoải mái.', '-', 'She sat on the couch to read a book.', 'Cô ấy ngồi trên sofa để đọc sách.'),  
('drip', '/drɪp/', 'Động từ', 'Verb', 'Nhỏ giọt', 'Chảy thành giọt từ một nguồn nước.', 'drip - dripped - dripped', 'The faucet was dripping all night.', 'Vòi nước đã nhỏ giọt suốt đêm.'),  
('elegant', '/ˈɛl.ɪ.ɡənt/', 'Tính từ', 'Adjective', 'Thanh lịch', 'Có phong cách và sự tinh tế.' , '-', 'She wore an elegant dress to the gala.', 'Cô ấy đã mặc một chiếc váy thanh lịch đến buổi tiệc.'),  
('fabric', '/ˈfæb.rɪk/', 'Danh từ', 'Noun', 'Vải', 'Chất liệu sử dụng để làm quần áo hoặc đồ nội thất.', '-', 'The dress was made of a soft fabric.', 'Chiếc váy được làm từ một loại vải mềm.'),  
('highlands', '/ˈhaɪ.ləndz/', 'Danh từ', 'Noun', 'Vùng cao', 'Một khu vực địa lý ở độ cao lớn, thường có đồi núi.', '-', 'The highlands are known for their stunning landscapes.', 'Vùng cao nổi tiếng với những cảnh đẹp tuyệt vời.'),  
('ivory', '/ˈaɪ.və.ri/', 'Danh từ', 'Noun', 'Ngà', 'Chất liệu cứng, màu kem, thường được lấy từ răng voi.', '-', 'Ivory is often used for making jewelry.', 'Ngà thường được sử dụng để làm trang sức.'),  
('mill', '/mɪl/', 'Danh từ', 'Noun', 'Cối xay', 'Một nhà máy dùng để chế biến ngũ cốc hoặc nguyên liệu thành sản phẩm.', '-', 'The mill produces flour from wheat.', 'Cối xay sản xuất bột từ lúa mì.'),  
('needle', '/ˈniː.dl/', 'Danh từ', 'Noun', 'Kim', 'Một công cụ nhỏ, sắc nhọn được dùng để khâu hoặc may.', '-', 'She used a needle and thread to fix the dress.', 'Cô ấy đã sử dụng kim và chỉ để sửa chiếc váy.'),  
('polish', '/ˈpɒl.ɪʃ/', 'Động từ', 'Verb', 'Đánh bóng', 'Giúp bề mặt trở nên bóng và sáng hơn.', 'polish - polished - polished', 'He polished the shoe until it shined.', 'Anh ấy đã đánh bóng giày cho đến khi nó sáng bóng.'),  
('sew', '/soʊ/', 'Động từ', 'Verb', 'May', 'Sử dụng kim và chỉ để kết nối các mảnh vải lại với nhau.', 'sew - sewed - sewn', 'She sews her own clothes.', 'Cô ấy tự may quần áo cho mình.'),  
('shed', '/ʃɛd/', 'Danh từ', 'Noun', 'Nhà kho', 'Một cấu trúc thường được dùng để lưu trữ hoặc bảo quản đồ vật.', '-', 'He keeps his tools in the shed.', 'Anh ấy giữ dụng cụ trong nhà kho.'),  
('thread', '/θrɛd/', 'Danh từ', 'Noun', 'Chỉ', 'Một sợi dai dùng trong may vá.', '-', 'She bought new thread for her sewing project.', 'Cô ấy đã mua chỉ mới cho dự án may vá của mình.'),  
('trim', '/trɪm/', 'Động từ', 'Verb', 'Cắt tỉa', 'Giảm bớt kích thước của một cái gì đó bằng cách cắt.', 'trim - trimmed - trimmed', 'He trimmed the bushes in the garden.', 'Anh ấy đã cắt tỉa những bụi cây trong vườn.'),  
('upwards', '/ˈʌp.wərdz/', 'Trạng từ', 'Adverb', 'Lên trên', 'Hướng về phía trên.', '-', 'The balloon rose upwards into the sky.', 'Bóng bay đã bay lên trên bầu trời.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('ail', '/eɪl/', 'Động từ', 'Verb', 'Bị ốm', 'Gặp vấn đề về sức khỏe.', 'ail - ailed - ailed', 'She has been ailing for several weeks.', 'Cô ấy đã bị ốm trong vài tuần.'),  
('ally', '/ˈæl.aɪ/', 'Danh từ', 'Noun', 'Đồng minh', 'Một người hoặc quốc gia hợp tác với người khác trong một mục tiêu chung.', '-', 'The countries formed an alliance with their allies.', 'Các quốc gia đã hình thành liên minh với các đồng minh của họ.'),  
('boast', '/boʊst/', 'Động từ', 'Verb', 'Khoe khoang', 'Nói về thành tích của mình với sự kiêu hãnh.', 'boast - boasted - boasted', 'He likes to boast about his achievements.', 'Anh ấy thích khoe khoang về những thành tựu của mình.'),  
('bounce', '/baʊns/', 'Động từ', 'Verb', 'Nảy', 'Nhảy lên và xuống hoặc từ bề mặt.', 'bounce - bounced - bounced', 'The ball bounces when it hits the ground.', 'Bóng nảy lên khi chạm đất.'),  
('bully', '/ˈbʊl.i/', 'Động từ', 'Verb', 'Bắt nạt', 'Đe dọa hoặc làm tổn thương một người yếu hơn.', 'bully - bullied - bullied', 'He was bullied at school for his appearance.', 'Anh ấy đã bị bắt nạt ở trường vì ngoại hình của mình.'),  
('carbohydrate', '/ˌkɑːr.boʊˈhaɪ.dreɪt/', 'Danh từ', 'Noun', 'Carbohydrate', 'Một loại chất dinh dưỡng cần thiết cho năng lượng.', '-', 'Bread is a good source of carbohydrates.', 'Bánh mì là nguồn cung cấp carbohydrate tốt.'),  
('crawl', '/krɔːl/', 'Động từ', 'Verb', 'Bò', 'Di chuyển bằng cách dùng tay và chân.', 'crawl - crawled - crawled', 'The baby crawled across the floor.', 'Đứa bé đã bò qua sàn nhà.'),  
