('scheme', '/skiːm/', 'Danh từ', 'Noun', 'Kế hoạch', 'Một phương pháp tổ chức hoặc một dự án được thiết kế để thực hiện một cái gì đó.', '-', 'He has a scheme to improve sales.', 'Anh ấy có một kế hoạch để cải thiện doanh số bán hàng.'),  
('slim', '/slɪm/', 'Tính từ', 'Adjective', 'Mảnh khảnh', 'Có kích thước nhỏ và thanh mảnh.', '-', 'She has a slim figure.', 'Cô ấy có thân hình mảnh khảnh.'),  
('stove', '/stoʊv/', 'Danh từ', 'Noun', 'Bếp', 'Một thiết bị tạo ra nhiệt để nấu thức ăn.', '-', 'The stove is used for cooking meals.', 'Bếp được sử dụng để nấu ăn.'),  
('theft', '/θɛft/', 'Danh từ', 'Noun', 'Trộm cắp', 'Hành động lấy cắp tài sản của người khác mà không có sự cho phép.', '-', 'The theft was reported to the police.', 'Vụ trộm đã được báo cáo cho cảnh sát.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('adolescent', '/ˌæd.əˈles.ənt/', 'Danh từ', 'Noun', 'Thanh thiếu niên', 'Người trong độ tuổi từ khoảng 13 đến 19.', '-', 'The adolescent stage is critical for personal development.', 'Giai đoạn thanh thiếu niên là rất quan trọng cho sự phát triển cá nhân.'),  
('aptitude', '/ˈæp.tɪ.tuːd/', 'Danh từ', 'Noun', 'Tố chất', 'Khả năng tự nhiên để học một cái gì đó hoặc làm một cái gì đó.', '-', 'She has a natural aptitude for music.', 'Cô ấy có tố chất tự nhiên cho âm nhạc.'),  
('compliment', '/ˈkɒmplɪmənt/', 'Danh từ', 'Noun', 'Lời khen', 'Một lời nói thể hiện sự khen ngợi.', '-', 'He received many compliments for his performance.', 'Anh ấy nhận được nhiều lời khen cho màn trình diễn của mình.'),  
('hinder', '/ˈhɪndər/', 'Động từ', 'Verb', 'Cản trở', 'Làm cho một cái gì đó trở nên khó khăn hơn hoặc ngăn chặn xảy ra.', 'hinder - hindered - hindered', 'His injuries hindered his performance.', 'Chấn thương của anh ấy đã cản trở màn trình diễn của anh ấy.'),  
('journalism', '/ˈdʒɜːrnəlɪzm/', 'Danh từ', 'Noun', 'Ngành báo chí', 'Nghề hoặc hoạt động tìm kiếm và truyền đạt thông tin đến công chúng.', '-', 'She chose journalism as her major.', 'Cô ấy chọn ngành báo chí làm chuyên ngành của mình.'),  
('jury', '/ˈdʒʊr.i/', 'Danh từ', 'Noun', 'Ban hội thẩm', 'Một nhóm người được chọn để nghe một vụ án và quyết định vấn đề liên quan.', '-', 'The jury found him not guilty.', 'Ban hội thẩm đã tuyên bố anh ta không có tội.'),  
('justice', '/ˈdʒʌstɪs/', 'Danh từ', 'Noun', 'Công lý', 'Nguyên tắc về sự công bằng và chính trực.', '-', 'Justice is important for a fair society.', 'Công lý là quan trọng đối với một xã hội công bằng.'),  
('liberty', '/ˈlɪbərti/', 'Danh từ', 'Noun', 'Tự do', 'Tình trạng hoặc quyền tự do làm những gì mình muốn.', '-', 'Liberty and freedom are essential human rights.', 'Tự do và quyền tự do là những quyền cơ bản của con người.'),  
('literary', '/ˈlɪtəreri/', 'Tính từ', 'Adjective', 'Văn học', 'Liên quan đến văn học.', '-', 'She has a strong literary background.', 'Cô ấy có nền tảng văn học vững chắc.'),  
('pharmacy', '/ˈfɑːrməsi/', 'Danh từ', 'Noun', 'Nhà thuốc', 'Một cửa hàng cung cấp thuốc và các sản phẩm y tế.', '-', 'He works at a local pharmacy.', 'Anh ấy làm việc tại một hiệu thuốc địa phương.'),  
('pill', '/pɪl/', 'Danh từ', 'Noun', 'Viên thuốc', 'Một dạng thuốc ở dạng viên.', '-', 'He took a pill for his headache.', 'Anh ấy đã uống một viên thuốc cho cơn đau đầu của mình.'),  
('presume', '/prɪˈzjuːm/', 'Động từ', 'Verb', 'Giả định', 'Giả định một điều gì đó là đúng mà không có bằng chứng rõ ràng.', 'presume - presumed - presumed', 'I presume you’re coming to the party.', 'Tôi giả định bạn sẽ đến bữa tiệc.'),  
('privacy', '/ˈprɪvəsi/', 'Danh từ', 'Noun', 'Quyền riêng tư', 'Quyền giữ cho thông tin cá nhân không bị tiết lộ.', '-', 'Personal privacy is a fundamental right.', 'Quyền riêng tư cá nhân là một quyền cơ bản.'),  
('punishment', '/ˈpʌnɪʃmənt/', 'Danh từ', 'Noun', 'Hình phạt', 'Hình thức trừng phạt một hành vi phạm pháp hoặc vi phạm quy định.', '-', 'He received a punishment for his actions.', 'Anh ta đã nhận hình phạt cho hành động của mình.'),  
('sensible', '/ˈsɛnsəbl/', 'Tính từ', 'Adjective', 'Hợp lý', 'Có sự phán xét tốt, có lý trí.', '-', 'It is sensible to save money.', 'Thật hợp lý khi tiết kiệm tiền.'),  
('slice', '/slaɪs/', 'Động từ', 'Verb', 'Cắt', 'Chia một cái gì đó ra thành những phần mỏng.', 'slice - sliced - sliced', 'She sliced the bread into pieces.', 'Cô ấy đã cắt bánh mì thành từng miếng.'),  
('sorrow', '/ˈsɔroʊ/', 'Danh từ', 'Noun', 'Nỗi buồn', 'Cảm giác đau khổ hoặc buồn bã.', '-', 'He felt sorrow at her loss.', 'Anh ấy cảm thấy nỗi buồn khi mất cô ấy.'),  
('straw', '/strɔː/', 'Danh từ', 'Noun', 'Rơm', 'Công cụ được làm từ ngũ cốc khô, thường dùng để làm ổ cho động vật.', '-', 'The barn was filled with straw.', 'Cái chuồng được lấp đầy bằng rơm.'),  
('swell', '/swɛl/', 'Động từ', 'Verb', 'Sưng', 'Trở nên lớn hơn, thường là do chứa thêm chất lỏng.', 'swell - swelled - swollen', 'His ankle began to swell after the injury.', 'Mắt cá chân của anh ấy bắt đầu sưng lên sau chấn thương.'),  
('tidy', '/ˈtaɪdi/', 'Tính từ', 'Adjective', 'Ngăn nắp', 'Gọn gàng, sạch sẽ.', '-', 'She prefers a tidy workspace.', 'Cô ấy thích một không gian làm việc ngăn nắp.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('affection', '/əˈfɛkʃən/', 'Danh từ', 'Noun', 'Tình cảm', 'Cảm giác yêu thương hoặc sự quan tâm sâu sắc đối với một người.', '-', 'She shows great affection for her family.', 'Cô ấy thể hiện tình cảm lớn cho gia đình.'),  
('agency', '/ˈeɪdʒənsi/', 'Danh từ', 'Noun', 'Cơ quan', 'Một tổ chức hoặc công ty thực hiện một chức năng hoặc dịch vụ nhất định.', '-', 'The travel agency helped us plan our trip.', 'Cơ quan du lịch đã giúp chúng tôi lên kế hoạch cho chuyến đi.'),  
('ash', '/æʃ/', 'Danh từ', 'Noun', 'Tro', 'Chất rắn màu xám hoặc đen còn lại sau khi đốt một vật nào đó.', '-', 'The ash fell to the ground after the fire.', 'Tro đã rơi xuống mặt đất sau khi đám cháy tắt.'),  
('confine', '/kənˈfaɪn/', 'Động từ', 'Verb', 'Giam giữ', 'Giới hạn hoặc giữ trong một không gian nhất định.', 'confine - confined - confined', 'The prisoners were confined to their cells.', 'Các tù nhân bị giam giữ trong phòng giam của họ.'),  
('dismiss', '/dɪsˈmɪs/', 'Động từ', 'Verb', 'Bác bỏ', 'Tránh xa, không xem xét hoặc cho phép rời bỏ.', 'dismiss - dismissed - dismissed', 'She chose to dismiss his arguments.', 'Cô ấy đã chọn bác bỏ các lập luận của anh ta.'),  
('erupt', '/ɪˈrʌpt/', 'Động từ', 'Verb', 'Phun trào', 'Xuất hiện đột ngột, thường nói về núi lửa.', 'erupt - erupted - erupted', 'The volcano may erupt at any time.', 'Núi lửa có thể phun trào vào bất kỳ lúc nào.'),  
('fate', '/feɪt/', 'Danh từ', 'Noun', 'Số phận', 'Số phận hoặc vận mệnh của một người.', '-', 'It was her fate to become a great artist.', 'Đó là số phận của cô ấy để trở thành một nghệ sĩ vĩ đại.'),  
('lava', '/ˈlɑːvə/', 'Danh từ', 'Noun', 'Dung nham', 'Dòng chất lỏng nóng áp suất từ bên trong trái đất khi núi lửa phun trào.', '-', 'The lava flowed down the mountainside.', 'Dung nham chảy xuống sườn núi.'),  
('miserable', '/ˈmɪzərəbəl/', 'Tính từ', 'Adjective', 'Khổ sở', 'Cảm thấy rất buồn hoặc không thỏa mãn.', '-', 'He felt miserable after losing his job.', 'Anh ấy cảm thấy khổ sở sau khi mất việc.'),  
('navigate', '/ˈnævɪɡeɪt/', 'Động từ', 'Verb', 'Dẫn đường', 'Đi qua hoặc điều hướng một không gian, thường là bằng cách sử dụng bản đồ hoặc giao diện.', 'navigate - navigated - navigated', 'They learned how to navigate through the city.', 'Họ đã học cách dẫn đường trong thành phố.'),  
('originate', '/əˈrɪdʒɪneɪt/', 'Động từ', 'Verb', 'Bắt nguồn', 'Bắt đầu hoặc khởi phát từ một nơi nào đó.', 'originate - originated - originated', 'This tradition originated in ancient times.', 'Truyền thống này bắt nguồn từ thời cổ đại.'),  
('remainder', '/rɪˈmeɪndər/', 'Danh từ', 'Noun', 'Phần còn lại', 'Phần còn lại sau khi đã trừ đi hoặc sử dụng một phần nào đó.', '-', 'Please keep the remainder of your homework for tomorrow.', 'Vui lòng giữ lại phần còn lại của bài tập về nhà cho ngày mai.'),  
('retrieve', '/rɪˈtriːv/', 'Động từ', 'Verb', 'Lấy lại', 'Lấy hoặc khôi phục một thứ gì đó đã mất.', 'retrieve - retrieved - retrieved', 'He managed to retrieve the lost document.', 'Anh ấy đã lấy lại được tài liệu đã mất.'),  
('shallow', '/ˈʃæloʊ/', 'Tính từ', 'Adjective', 'Nông', 'Không sâu, thường nói về nước hoặc lỗ.', '-', 'The pool is too shallow for diving.', 'Bể bơi quá nông để nhảy xuống.'),  
('slope', '/sloʊp/', 'Danh từ', 'Noun', 'Dốc', 'Một mặt nghiêng hoặc bề mặt không phẳng.', '-', 'The slope of the hill was steep.', 'Dốc của ngọn đồi thì dốc.'),  
('span', '/spæn/', 'Danh từ', 'Noun', 'Tầm với', 'Khoảng thời gian hoặc khoảng cách giữa hai điểm.', '-', 'The project will span two years.', 'Dự án sẽ kéo dài trong hai năm.'),  
('superstition', '/ˌsuːpərˈstɪʃən/', 'Danh từ', 'Noun', 'Mê tín', 'Tin tưởng vào những điều huyền bí không có cơ sở khoa học.', '-', 'Many cultures have their own superstitions.', 'Nhiều nền văn hóa có những điều mê tín của riêng mình.'),  
('sympathy', '/ˈsɪmpəθi/', 'Danh từ', 'Noun', 'Sự cảm thông', 'Cảm giác quan tâm hoặc thương hại cho ai đó trong hoàn cảnh khó khăn.', '-', 'She expressed her sympathy for the victims.', 'Cô ấy thể hiện sự cảm thông cho các nạn nhân.'),  
('vibrate', '/ˈvaɪbreɪt/', 'Động từ', 'Verb', 'Rung', 'Di chuyển nhanh chóng qua lại hoặc lên xuống.', 'vibrate - vibrated - vibrated', 'The phone began to vibrate.', 'Điện thoại bắt đầu rung.'),  
('wander', '/ˈwɔːndər/', 'Động từ', 'Verb', 'Đi lang thang', 'Đi một cách không có mục đích, thường di chuyển từ nơi này sang nơi khác mà không có kế hoạch.', 'wander - wandered - wandered', 'They like to wander in the park.', 'Họ thích đi lang thang trong công viên.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('armor', '/ˈɑːrmər/', 'Danh từ', 'Noun', 'Giáp', 'Vật liệu bảo vệ cho cơ thể, thường được sử dụng trong các trận chiến.', '-', 'The knight wore heavy armor into battle.', 'Hiệp sĩ mặc giáp nặng trong trận chiến.'),  
('blaze', '/bleɪz/', 'Động từ', 'Verb', 'Bùng cháy', 'Tạo ra một ngọn lửa lớn, sáng rực.', 'blaze - blazed - blazed', 'The fire continued to blaze throughout the night.', 'Ngọn lửa tiếp tục bùng cháy suốt đêm.'),  
('boom', '/buːm/', 'Động từ', 'Verb', 'Nổ vang', 'Phát ra tiếng nổ lớn hoặc tiếng vang.', 'boom - boomed - boomed', 'The cannon boomed across the battlefield.', 'Khẩu đại bác nổ vang trên chiến trường.'),  
('cliff', '/klɪf/', 'Danh từ', 'Noun', 'Vách đá', 'Một bề mặt dốc đứng của đất hoặc đá, thường ở ven biển.', '-', 'They stood on the edge of the cliff.', 'Họ đứng ở mép vách đá.'),  
('flame', '/fleɪm/', 'Danh từ', 'Noun', 'Ngọn lửa', 'Ánh sáng và nhiệt từ một vật đang cháy.', '-', 'The flame flickered in the darkness.', 'Ngọn lửa nhấp nháy trong bóng tối.'),  
('independence', '/ˌɪndɪˈpɛndəns/', 'Danh từ', 'Noun', 'Độc lập', 'Trạng thái của việc không bị kiểm soát hoặc phụ thuộc vào ai khác.', '-', 'The country celebrated its independence day.', 'Quốc gia đã kỷ niệm ngày độc lập của mình.'),  
('invasion', '/ɪnˈveɪʒən/', 'Danh từ', 'Noun', 'Cuộc xâm lược', 'Hành động xâm chiếm một lãnh thổ hoặc khu vực.', '-', 'The invasion caused widespread panic.', 'Cuộc xâm lược đã gây ra sự hoảng loạn rộng rãi.'),  
('knight', '/naɪt/', 'Danh từ', 'Noun', 'Hiệp sĩ', 'Một chiến binh được phong tước, thường mặc giáp và phục vụ một lãnh chúa.', '-', 'The knight rescued the princess from danger.', 'Hiệp sĩ đã cứu công chúa khỏi nguy hiểm.'),  
('lightning', '/ˈlaɪtnɪŋ/', 'Danh từ', 'Noun', 'Sét', 'Ánh sáng chói lòa trong bầu trời do điện trong mây.', '-', 'The lightning struck the tree.', 'Sấm sét đã đánh vào cây.'),  
('rebel', '/ˈrɛbəl/', 'Danh từ', 'Noun', 'Kẻ nổi loạn', 'Người đứng lên chống lại quyền lực hoặc chính phủ.', '-', 'The rebels fought for their freedom.', 'Những kẻ nổi loạn đã chiến đấu vì tự do của họ.'),  
('retreat', '/rɪˈtriːt/', 'Động từ', 'Verb', 'Rút lui', 'Rời khỏi một tình huống khó khăn hoặc nguy hiểm;', 'retreat - retreated - retreated', 'The army decided to retreat from the battlefield.', 'Quân đội quyết định rút lui khỏi chiến trường.'),
('revolution', '/ˌrɛvəˈluːʃən/', 'Danh từ', 'Noun', 'Cách mạng', 'Thay đổi lớn về chính trị hoặc xã hội.', '-', 'The revolution changed the country''s government.', 'Cuộc cách mạng đã thay đổi chính phủ của quốc gia.'),  
('spear', '/spɪr/', 'Danh từ', 'Noun', 'Ngọn giáo', 'Một loại vũ khí dài có đầu nhọn, được sử dụng để đâm hoặc ném.', '-', 'He threw the spear with great force.', 'Anh ấy đã ném ngọn giáo với sức mạnh lớn.'),  
('steep', '/stiːp/', 'Tính từ', 'Adjective', 'Dốc', 'Có độ nghiêng lớn và cao, thường nói về ngọn đồi hoặc bề mặt.', '-', 'The slope was too steep to climb.', 'Dốc quá dốc để leo lên.'),  
('summit', '/ˈsʌmɪt/', 'Danh từ', 'Noun', 'đỉnh núi', 'Điểm cao nhất của một ngọn núi.', '-', 'They reached the summit of the mountain.', 'Họ đã đạt đến đỉnh núi.'),  
('thunder', '/ˈθʌndər/', 'Danh từ', 'Noun', 'Sấm', 'Âm thanh lớn phát ra từ bầu trời sau khi có sét.', '-', 'The thunder rumbled in the distance.', 'Tiếng sấm gầm gừ ở phía xa.'),  
('troops', '/truːps/', 'Danh từ', 'Noun', 'Quân đội', 'Một nhóm lính hoặc quân đội.', '-', 'The troops were deployed to the front lines.', 'Các quân đội đã được triển khai tới chiến tuyến.'),  
('warrior', '/ˈwɔːriər/', 'Danh từ', 'Noun', 'Chiến binh', 'Người chiến đấu trong chiến tranh hoặc cho một lý tưởng.', '-', 'The warrior bravely faced the enemy.', 'Chiến binh đã dũng cảm đối mặt với kẻ thù.'),  
('withdraw', '/wɪðˈdrɔː/', 'Động từ', 'Verb', 'Rút lui', 'Rời khỏi một tình huống hoặc vị trí.', 'withdraw - withdrew - withdrawn', 'He decided to withdraw from the competition.', 'Anh ấy đã quyết định rút lui khỏi cuộc thi.'),  
('yield', '/jiːld/', 'Động từ', 'Verb', 'Đầu hàng', 'Chấp nhận để điều gì đó xảy ra, thường là nhượng bộ.', 'yield - yielded - yielded', 'They had to yield to the demands of the protesters.', 'Họ buộc phải đầu hàng trước yêu cầu của những người biểu tình.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('bench', '/bɛnʧ/', 'Danh từ', 'Noun', 'Ghế dài', 'Một chiếc ghế dài, thường được dùng trong công viên hoặc sân thể thao.', '-', 'They sat on the bench to rest.', 'Họ ngồi trên ghế dài để nghỉ ngơi.'),  
('confront', '/kənˈfrʌnt/', 'Động từ', 'Verb', 'Đối mặt', 'Đối diện và nói chuyện với một người về vấn đề nào đó.', 'confront - confronted - confronted', 'She decided to confront her fears.', 'Cô ấy quyết định đối mặt với nỗi sợ hãi của mình.'),  
('daisy', '/ˈdeɪ.zi/', 'Danh từ', 'Noun', 'Hoa cúc', 'Một loại hoa có cánh trắng và nhụy vàng ở giữa.', '-', 'The field was filled with daisies.', 'Cánh đồng đầy hoa cúc.'),  
('dispute', '/dɪˈspjuːt/', 'Danh từ', 'Noun', 'Tranh chấp', 'Một cuộc tranh luận hoặc bất đồng ý kiến.', '-', 'There was a dispute over the property.', 'Đã có một tranh chấp về tài sản.'),  
