INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('Aborigine', '/ˌæbəˈrɪdʒəni/', 'Danh từ', 'Noun', 'Người bản địa', 'Người dân đầu tiên của một vùng đất, đặc biệt là ở Úc.', '-', 'The Aborigine culture has a rich history.', 'Văn hóa của người bản địa có một lịch sử phong phú.'),  
('ban', '/bæn/', 'Động từ', 'Verb', 'Cấm', 'Đưa ra lệnh không cho phép điều gì đó.', 'ban - banned - banned', 'The school decided to ban sugary drinks.', 'Trường học quyết định cấm đồ uống có đường.'),  
('cautious', '/ˈkɔːʃəs/', 'Tính từ', 'Adjective', 'Thận trọng', 'Hành động một cách cẩn thận để tránh rủi ro hoặc nguy hiểm.', '-', 'He was cautious while crossing the street.', 'Anh ấy đã thận trọng khi băng qua đường.'),  
('confess', '/kənˈfɛs/', 'Động từ', 'Verb', 'Thú nhận', 'Thừa nhận đã làm gì sai hoặc cảm xúc của mình.', 'confess - confessed - confessed', 'She decided to confess her mistake.', 'Cô ấy đã quyết định thú nhận lỗi sai của mình.'),  
('cottage', '/ˈkɒtɪdʒ/', 'Danh từ', 'Noun', 'Nhà tranh', 'Một ngôi nhà nhỏ, thường ở nông thôn.', '-', 'They spent the weekend in a cozy cottage.', 'Họ đã dành cả cuối tuần trong một ngôi nhà tranh ấm cúng.'),  
('daytime', '/ˈdeɪtaɪm/', 'Danh từ', 'Noun', 'Ban ngày', 'Khoảng thời gian khi mặt trời ánh sáng, từ sáng đến chiều.', '-', 'We usually go hiking during the daytime.', 'Chúng tôi thường đi leo núi vào ban ngày.'),  
('desperate', '/ˈdɛspərət/', 'Tính từ', 'Adjective', ' Tuyệt vọng', 'Cảm thấy không có hy vọng và sẵn sàng làm bất cứ điều gì.', '-', 'She was desperate to find a solution.', 'Cô ấy đã tuyệt vọng tìm kiếm một giải pháp.'),  
