('poster', '/ˈpoʊstər/', 'Danh từ', 'Noun', 'Áp phích', 'Một tấm giấy lớn được dùng để quảng bá hoặc truyền đạt thông điệp.', '-', 'The poster advertised the upcoming concert.', 'Áp phích đã quảng bá cho buổi hòa nhạc sắp tới.'),  
('scratch', '/skrætʃ/', 'Động từ', 'Verb', 'Cào', 'Làm xước bề mặt bằng cách kéo một vật sắc bén trên đó.', 'scratch - scratched - scratched', 'The cat likes to scratch the furniture.', 'Con mèo thích cào đồ đạc.'),  
('symphony', '/ˈsɪmfəni/', 'Danh từ', 'Noun', 'Giao hưởng', 'Một tác phẩm âm nhạc lớn, thường được viết cho dàn nhạc.', '-', 'The symphony was performed by the orchestra.', 'Bản giao hưởng đã được trình diễn bởi dàn nhạc.'),  
('widow', '/ˈwɪdoʊ/', 'Danh từ', 'Noun', 'Góa phụ', 'Một người phụ nữ có chồng đã qua đời.', '-', 'The widow received support from her community.', 'Người góa phụ đã nhận được sự hỗ trợ từ cộng đồng của mình.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('circulate', '/ˈsɜːr.kjʊ.leɪt/', 'Động từ', 'Verb', 'Tuần hoàn', 'Di chuyển khắp nơi trong một không gian hoặc hệ thống.', 'circulate - circulated - circulated', 'The news began to circulate quickly.', 'Tin tức bắt đầu lan truyền nhanh chóng.'),  
('consequent', '/ˈkɒnsɪkwənt/', 'Tính từ', 'Adjective', 'Phát sinh', 'Xảy ra như một kết quả của một sự kiện hoặc hành động trước đó.', '-', 'The consequent changes shocked everyone.', 'Các thay đổi phát sinh đã khiến mọi người sốc.'),  
('derive', '/dɪˈraɪv/', 'Động từ', 'Verb', 'Xuất phát', 'Lấy được từ một nguồn gốc hay nguyên nhân.', 'derive - derived - derived', 'Many English words derive from Latin.', 'Nhiều từ tiếng Anh xuất phát từ tiếng Latin.'),  
