('suburb', '/ˈsʌbɜːrb/', 'Danh từ', 'Noun', 'Ngoại ô', 'Khu vực dân cư nằm bên ngoài thành phố lớn.', '-', 'They live in a suburb of the city.', 'Họ sống ở một ngoại ô của thành phố.'),  
('tide', '/taɪd/', 'Danh từ', 'Noun', 'Thủy triều', 'Sự lên và xuống của nước biển do ảnh hưởng của mặt trăng và mặt trời.', '-', 'The tide goes out during the day.', 'Thủy triều rút ra trong suốt cả ngày.'),  
('tragedy', '/ˈtrædʒ.ə.di/', 'Danh từ', 'Noun', 'Thảm kịch', 'Một sự kiện đau thương hoặc bi thảm, thường trong văn học hoặc sân khấu.', '-', 'The play ends in tragedy.', 'Vở kịch kết thúc bằng một thảm kịch.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('bomb', '/bɑːm/', 'Danh từ', 'Noun', 'Bom', 'Một thiết bị nổ được thiết kế để gây thiệt hại.', '-', 'The bomb exploded in the crowded area.', 'Quả bom đã phát nổ ở khu vực đông người.'),  
('certificate', '/sərˈtɪfɪkət/', 'Danh từ', 'Noun', 'Chứng chỉ', 'Một tài liệu chính thức chứng minh bạn đã hoàn thành khóa học hoặc đạt được tiêu chuẩn nào đó.', '-', 'He received a certificate for completing the course.', 'Anh ấy đã nhận được chứng chỉ vì hoàn thành khóa học.'),  
('circumstance', '/ˈsɜːrkəmstæns/', 'Danh từ', 'Noun', 'Hoàn cảnh', 'Một điều kiện hoặc tình huống liên quan đến một sự kiện.', '-', 'Under normal circumstances, we would celebrate.', 'Dưới các hoàn cảnh bình thường, chúng tôi sẽ ăn mừng.'),  
('coffin', '/ˈkɒf.ɪn/', 'Danh từ', 'Noun', 'Quan tài', 'Một chiếc hộp dùng để chôn người đã khuất.', '-', 'The coffin was made of polished wood.', 'Quan tài được làm từ gỗ bóng.'),  
('cope', '/koʊp/', 'Động từ', 'Verb', 'Đương đầu', 'Đối phó với khó khăn hoặc thách thức.', 'cope - coped - coped', 'She learned to cope with stress in healthy ways.', 'Cô ấy đã học cách đối phó với căng thẳng một cách lành mạnh.'),  
('criticism', '/ˈkrɪtɪsɪzm/', 'Danh từ', 'Noun', 'Phê bình', 'Ý kiến đánh giá về một tác phẩm hoặc hành động, thường là tiêu cực.', '-', 'The film received harsh criticism from critics.', 'Bộ phim đã nhận được phê bình gay gắt từ các nhà phê bình.'),  
('devastate', '/ˈdɛv.ə.steɪt/', 'Động từ', 'Verb', 'Phá hủy', 'Gây ra thiệt hại lớn hoặc sự tàn phá.', 'devastate - devastated - devastated', 'The storm devastated the coastal town.', 'Cơn bão đã phá hủy thị trấn ven biển.'),  
('frown', '/fraʊn/', 'Động từ', 'Verb', 'Nhăn mặt', 'Khuôn mặt nhăn lại vì lo lắng hoặc không hài lòng.', 'frown - frowned - frowned', 'He frowned when he heard the bad news.', 'Anh ấy đã nhăn mặt khi nghe tin xấu.'),  
('gaze', '/ɡeɪz/', 'Động từ', 'Verb', 'Nhìn chằm chằm', 'Nhìn một cách chăm chú, thường thể hiện sự ngạc nhiên hoặc quan tâm.', 'gaze - gazed - gazed', 'She gazed at the stars in awe.', 'Cô ấy đã nhìn chằm chằm vào các vì sao trong sự kinh ngạc.'),  
('glance', '/ɡlæns/', 'Động từ', 'Verb', 'Liếc nhìn', 'Nhìn thoáng qua hoặc nhanh chóng.', 'glance - glanced - glanced', 'He took a quick glance at his watch.', 'Anh ấy đã liếc nhìn đồng hồ của mình một cách nhanh chóng.'),  
('grief', '/ɡriːf/', 'Danh từ', 'Noun', 'Nỗi buồn', 'Cảm giác buồn bã sâu sắc, thường do mất mát.', '-', 'She felt overwhelming grief after her loss.', 'Cô ấy cảm thấy nỗi buồn tràn ngập sau sự mất mát của mình.'),  
('groom', '/ɡruːm/', 'Danh từ', 'Noun', 'Chú rể', 'Người đàn ông trong một buổi lễ kết hôn.', '-', 'The groom looked nervous before the wedding.', 'Chú rể trông lo lắng trước buổi lễ cưới.'),  
('license', '/ˈlaɪ.səns/', 'Danh từ', 'Noun', 'Giấy phép', 'Một tài liệu cho phép ai đó làm một hoạt động hợp pháp nào đó.', '-', 'You need a valid driver''s license to drive.', 'Bạn cần một giấy phép lái xe hợp lệ để lái xe.'),  
('microscope', '/ˈmaɪ.krəˌskoʊp/', 'Danh từ', 'Noun', 'Kính hiển vi', 'Một thiết bị quang học dùng để quan sát vật thể nhỏ mà mắt thường không nhìn thấy được.', '-', 'The scientist examined the samples under the microscope.', 'Nhà khoa học đã kiểm tra các mẫu dưới kính hiển vi.'),  
('nuclear', '/ˈnjuː.klɪər/', 'Tính từ', 'Adjective', 'Hạt nhân', 'Liên quan đến hoặc sử dụng năng lượng từ hạt nhân.', '-', 'Nuclear energy is a powerful energy source.', 'Năng lượng hạt nhân là một nguồn năng lượng mạnh mẽ.'),  
('portray', '/pɔːrˈtreɪ/', 'Động từ', 'Verb', 'Khắc họa', 'Mô tả hoặc vẽ một hình ảnh của một người hoặc vật trong nghệ thuật.', 'portray - portrayed - portrayed', 'The artist wanted to portray the beauty of nature.', 'Nghệ sĩ muốn khắc họa vẻ đẹp của thiên nhiên.'),  
('rotate', '/roʊˈteɪt/', 'Động từ', 'Verb', 'Xoay', 'Quay xung quanh một điểm hoặc một trục.', 'rotate - rotated - rotated', 'Please rotate the objects in the display case.', 'Xin hãy xoay các vật trong tủ trưng bày.'),  
('souvenir', '/ˌsuː.vəˈnɪr/', 'Danh từ', 'Noun', 'Quà lưu niệm', 'Một vật lưu trữ để nhớ lại một địa điểm hoặc sự kiện.', '-', 'She bought a souvenir from her trip.', 'Cô ấy đã mua một món quà lưu niệm từ chuyến đi của mình.'),  
('submarine', '/ˈsʌb.mə.riːn/', 'Danh từ', 'Noun', 'Tàu ngầm', 'Một loại phương tiện di chuyển dưới nước, thường dùng trong quân sự.', '-', 'The submarine can dive to great depths.', 'Tàu ngầm có thể lặn xuống độ sâu lớn.'),  
('trace', '/treɪs/', 'Động từ', 'Verb', 'Theo dõi', 'Tìm hoặc phát hiện ra dấu hiệu hoặc bằng chứng của điều gì đó.', 'trace - traced - traced', 'The detective tried to trace the origin of the clue.', 'Thám tử cố gắng theo dõi nguồn gốc của manh mối.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('appliance', '/əˈplaɪəns/', 'Danh từ', 'Noun', 'Thiết bị', 'Máy móc hoặc dụng cụ dùng trong gia đình để thực hiện công việc cụ thể.', '-', 'The kitchen is filled with modern appliances.', 'Nhà bếp được trang bị đầy đủ các thiết bị hiện đại.'),  
('basin', '/ˈbeɪ.sən/', 'Danh từ', 'Noun', 'Bồn', 'Một cái bát lớn hoặc bồn rửa dùng để chứa nước hoặc thực phẩm.', '-', 'She filled the basin with water for washing.', 'Cô ấy đã đổ nước vào bồn để rửa.'),  
('broom', '/bruːm/', 'Danh từ', 'Noun', 'Chổi', 'Một dụng cụ với cán dài và các sợi cứng, dùng để quét dọn.', '-', 'He used a broom to sweep the floor.', 'Anh ấy đã sử dụng chổi để quét sàn nhà.'),  
('caterpillar', '/ˈkæt.ər.pɪl.ər/', 'Danh từ', 'Noun', 'Sâu bướm', 'Giai đoạn trong vòng đời của loài bướm, trước khi biến thành nhộng.', '-', 'The caterpillar turned into a beautiful butterfly.', 'Con sâu bướm đã hóa thành một con bướm xinh đẹp.'),  
('cupboard', '/ˈkʌb.əd/', 'Danh từ', 'Noun', 'Tủ', 'Một loại tủ có cánh cửa, thường dùng để lưu trữ đồ vật.', '-', 'The dishes are in the cupboard above the sink.', 'Các món ăn nằm trong tủ trên bồn rửa.'),  
('delicate', '/ˈdɛlɪkət/', 'Tính từ', 'Adjective', 'Mỏng manh', 'Nhạy cảm một cách dễ vỡ hoặc yêu cầu sự chú ý, khéo léo.', '-', 'Handle the delicate fabric with care.', 'Hãy xử lý vải mỏng manh một cách cẩn thận.'),  
('emerge', '/ɪˈmɜːrdʒ/', 'Động từ', 'Verb', 'Xuất hiện', 'Đến hoặc trở nên rõ ràng, thường sau một khoảng thời gian ẩn náu.', 'emerge - emerged - emerged', 'The sun will emerge from behind the clouds.', 'Mặt trời sẽ xuất hiện sau những đám mây.'),  
('handicap', '/ˈhændɪkæp/', 'Danh từ', 'Noun', 'Khuyết tật', 'Một tình trạng hoặc khó khăn vật lý dẫn đến hạn chế khả năng của một người.', '-', 'He faced many challenges due to his handicap.', 'Anh ấy phải đối mặt với nhiều thách thức do khuyết tật của mình.'),  
('hook', '/hʊk/', 'Danh từ', 'Noun', 'Móc', 'Một vật nhỏ, thường hình dạng cong, dùng để treo hoặc giữ một cái gì đó.', '-', 'She hung her coat on the hook by the door.', 'Cô ấy đã treo áo khoác lên móc bên cửa.'),  
('hop', '/hɒp/', 'Động từ', 'Verb', 'Nhảy', 'Bước nhảy ngắn và nhẹ nhàng, thường chỉ bằng một chân.', 'hop - hopped - hopped', 'The rabbit began to hop across the field.', 'Con thỏ bắt đầu nhảy qua cánh đồng.'),  
('laundry', '/ˈlɔːndri/', 'Danh từ', 'Noun', 'Giặt ủi', 'Quá trình rửa sạch quần áo và vải vóc.', '-', 'I have to do the laundry this weekend.', 'Tôi phải giặt ủi quần áo vào cuối tuần này.'),  
('pursue', '/pərˈsuː/', 'Động từ', 'Verb', 'Theo đuổi', 'Theo đuổi một mục tiêu hoặc một người nào đó.', 'pursue - pursued - pursued', 'She decided to pursue her dreams.', 'Cô ấy quyết định theo đuổi giấc mơ của mình.'),  
('reluctant', '/rɪˈlʌktənt/', 'Tính từ', 'Adjective', 'Miễn cưỡng', 'Không sẵn lòng hoặc không muốn làm điều gì đó.', '-', 'He was reluctant to leave the comfort of his home.', 'Anh ấy miễn cưỡng rời khỏi sự thoải mái của ngôi nhà mình.'),  
('sleeve', '/sliːv/', 'Danh từ', 'Noun', 'Tay áo', 'Phần của áo che khuỷu tay hoặc cánh tay.', '-', 'Her shirt has long sleeves.', 'Áo của cô ấy có tay áo dài.'),  
('spine', '/spaɪn/', 'Danh từ', 'Noun', 'Cột sống', 'Một chuỗi các xương nhỏ dựng đứng ở lưng, hỗ trợ cơ thể.', '-', 'She injured her spine in the accident.', 'Cô ấy bị thương ở cột sống trong tai nạn.'),  
('stain', '/steɪn/', 'Danh từ', 'Noun', 'Vết bẩn', 'Một vết bẩn hoặc dấu trên bề mặt do chất lỏng hoặc bám bẩn.', '-', 'There was a stain on the carpet.', 'Có một vết bẩn trên thảm.'),  
('strip', '/strɪp/', 'Động từ', 'Verb', 'Lột', 'Tách ra hoặc loại bỏ một lớp hoặc phần nào đó.', 'strip - stripped - stripped', 'He had to strip the paint from the old furniture.', 'Anh ấy đã phải lột lớp sơn từ đồ nội thất cũ.'),  
('swear', '/swɛr/', 'Động từ', 'Verb', 'Thề', 'Cam kết hoặc hứa điều gì đó, đôi khi bằng cách sử dụng ngôn từ mạnh mẽ.', 'swear - swore - sworn', 'He swore to tell the truth.', 'Anh ấy đã thề nói sự thật.'),  
('swing', '/swɪŋ/', 'Động từ', 'Verb', 'Đu đưa', 'Di chuyển qua lại hoặc từ bên này sang bên kia.', 'swing - swung - swung', 'The child loves to swing on the playground.', 'Đứa trẻ thích đu đưa trên sân chơi.'),  
('utilize', '/ˈjuːtəlaɪz/', 'Động từ', 'Verb', 'Sử dụng', 'Sử dụng một cái gì đó một cách hữu ích hoặc hiệu quả.', 'utilize - utilized - utilized', 'We need to utilize our resources wisely.', 'Chúng ta cần sử dụng tài nguyên của mình một cách khôn ngoan.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('aroma', '/əˈroʊmə/', 'Danh từ', 'Noun', 'Mùi thơm', 'Mùi hương dễ chịu của một chất lỏng hoặc chất rắn', '-', 'The aroma of freshly brewed coffee filled the room.', 'Mùi thơm của cà phê mới pha tràn ngập căn phòng.'),  
('beverage', '/ˈbɛvərɪdʒ/', 'Danh từ', 'Noun', 'Đồ uống', 'Một loại chất lỏng được tiêu thụ để giải khát', '-', 'Water is the most common beverage consumed worldwide.', 'Nước là đồ uống phổ biến nhất được tiêu thụ trên toàn thế giới.'),  
('cluster', '/ˈklʌstər/', 'Danh từ', 'Noun', 'Cụm', 'Nhóm các đối tượng gần nhau', '-', 'A cluster of grapes hung from the vine.', 'Một cụm nho treo lủng lẳng từ cây nho.'),  
('combine', '/kəmˈbaɪn/', 'Động từ', 'Verb', 'Kết hợp', 'Kết hợp hai hay nhiều đối tượng lại với nhau', 'combine - combined - combined', 'You can combine these two chemicals to create a reaction.', 'Bạn có thể kết hợp hai hóa chất này để tạo ra một phản ứng.'),  
('condensed', '/kənˈdɛnst/', 'Tính từ', 'Adjective', 'Ngưng tụ', 'Chất lỏng được nén lại', '-', 'The condensed milk is often used in desserts.', 'Sữa đặc thường được sử dụng trong các món tráng miệng.'),  
('contemporary', '/kənˈtɛmpəˌrɛri/', 'Tính từ', 'Adjective', 'Đương đại', 'Đang sống cùng thời', '-', 'Her art style is very contemporary and well-received.', 'Phong cách nghệ thuật của cô ấy rất đương đại và được đón nhận tốt.'),  
('cultivate', '/ˈkʌltɪˌveɪt/', 'Động từ', 'Verb', 'Nuôi trồng', 'Gieo trồng và chăm sóc cây cối', 'cultivate - cultivated - cultivated', 'Farmers cultivate crops to sell in the market.', 'Nông dân nuôi trồng cây cối để bán ở thị trường.'),  
('divine', '/dɪˈvaɪn/', 'Tính từ', 'Adjective', 'Thần thánh', 'Liên quan đến thần thánh, siêu nhiên', '-', 'The meal was simply divine!', 'Bữa ăn này thực sự là hoàn hảo!'),  
('humid', '/ˈhjuːmɪd/', 'Tính từ', 'Adjective', 'Âm ẩm', 'Có độ ẩm cao', '-', 'The humid weather makes it feel hotter.', 'Thời tiết ẩm ướt làm cho cảm giác nóng hơn.'),  
('odor', '/ˈoʊdər/', 'Danh từ', 'Noun', 'Mùi', 'Hơi thở có thể cảm nhận được bởi khứu giác', '-', 'The odor of rotten eggs filled the air.', 'Mùi trứng thối lan tỏa trong không khí.'),  
('palate', '/ˈpælət/', 'Danh từ', 'Noun', 'Vòm miệng', 'Phần trên bên trong miệng', '-', 'She has a sophisticated palate for wine.', 'Cô ấy có một vòm miệng tinh tế cho rượu vang.'),  
('paradise', '/ˈpærədaɪs/', 'Danh từ', 'Noun', 'Thiên đường', 'Nơi đẹp tuyệt vời, nơi lý tưởng', '-', 'This beach is truly a paradise for tourists.', 'Bãi biển này thực sự là thiên đường cho khách du lịch.'),  
('plantation', '/plænˈteɪʃən/', 'Danh từ', 'Noun', 'Đồn điền', 'Vùng trồng cây lớn', '-', 'The plantation is known for its sugar cane.', 'Đồn điền nổi tiếng với mía.'),  
('rapid', '/ˈræpɪd/', 'Tính từ', 'Adjective', 'Nhanh chóng', 'Diễn ra hoặc xảy ra nhanh', '-', 'There was a rapid increase in temperature.', 'Đã có một sự tăng nhiệt độ nhanh chóng.'),  
('rate', '/reɪt/', 'Danh từ', 'Noun', 'Tỷ lệ', 'Sự đo lường tần suất', '-', 'The interest rate has dropped this year.', 'Tỷ lệ lãi suất đã giảm trong năm nay.'),  
('soothing', '/ˈsuːðɪŋ/', 'Tính từ', 'Adjective', 'Làm dịu', 'Giúp tạo cảm giác thoải mái, thư giãn', '-', 'The soothing music helped her relax.', 'Âm nhạc làm dịu giúp cô ấy thư giãn.'),  
('subtle', '/ˈsʌtəl/', 'Tính từ', 'Adjective', 'Tinh tế', 'Khó nhận thấy; mỏng manh', '-', 'There was a subtle difference between the two colors.', 'Có một sự khác biệt tinh tế giữa hai màu sắc.'),  
('texture', '/ˈtɛkʃər/', 'Danh từ', 'Noun', 'Kết cấu', 'Cảm giác bề mặt của một vật thể', '-', 'The texture of the fabric is very soft.', 'Kết cấu của vải rất mềm mại.'),  
('toxic', '/ˈtɑːksɪk/', 'Tính từ', 'Adjective', 'Độc hại', 'Có khả năng gây hại cho sức khỏe', '-', 'The chemical is toxic if ingested.', 'Hóa chất là độc hại nếu nuốt phải.'),  
('vary', '/ˈvɛri/', 'Động từ', 'Verb', 'Thay đổi', 'Diễn ra một cách khác nhau', 'vary - varied - varied', 'The prices may vary depending on the season.', 'Các mức giá có thể thay đổi tùy thuộc vào mùa.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('accident', '/ˈæksɪdənt/', 'Danh từ', 'Noun', 'Tai nạn', 'Sự cố bất ngờ gây ra thiệt hại', '-', 'The accident on the highway caused a major traffic jam.', 'Tai nạn trên cao tốc gây ra tắc đường nghiêm trọng.'),  
('admiral', '/ˈædmərəl/', 'Danh từ', 'Noun', 'Đô đốc', 'Cấp bậc cao trong hải quân', '-', 'The admiral commanded the entire fleet.', 'Đô đốc chỉ huy toàn bộ hạm đội.'),  
