INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('civilization', '/ˌsɪvəlʌɪˈzeɪʃən/', 'Danh từ', 'Noun', 'Nền văn minh', 'Một xã hội tổ chức tốt với kiến thức, nghệ thuật, và văn hóa phát triển.', '-', 'The rise of civilization led to advancements in technology.', 'Sự phát triển của nền văn minh đã dẫn đến những tiến bộ trong công nghệ.'),  
