INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('automobile', '/ˈɔːtəˌmoʊbiːl/', 'Danh từ', 'Noun', 'Xe ô tô', 'Một phương tiện giao thông có bốn bánh, thường được sử dụng để vận chuyển người.', '-', 'The automobile revolutionized transportation in the 20th century.', 'Xe ô tô đã cách mạng hóa giao thông vào thế kỷ 20.'),  
('candidate', '/ˈkændɪˌdeɪt/', 'Danh từ', 'Noun', 'Ứng cử viên', 'Một người tham gia một cuộc thi, bầu cử hoặc tuyển dụng.', '-', 'She is a strong candidate for the job.', 'Cô ấy là một ứng cử viên mạnh cho công việc.'),  
('confidential', '/ˌkɒnfɪˈdɛnʃəl/', 'Tính từ', 'Adjective', 'Bí mật', 'Thông tin mà chỉ một số người được phép biết.', '-', 'The documents are marked as confidential.', 'Các tài liệu được đánh dấu là bí mật.'),  
('corporate', '/ˈkɔːr.pɚ.ɪt/', 'Tính từ', 'Adjective', 'Doanh nghiệp', 'Liên quan đến công ty hoặc tổ chức kinh doanh.', '-', 'They are looking for corporate sponsorship.', 'Họ đang tìm kiếm tài trợ từ các doanh nghiệp.'),  
('enhance', '/ɪnˈhæns/', 'Động từ', 'Verb', 'Tăng cường', 'Cải thiện chất lượng hoặc giá trị của một cái gì đó.', 'enhance - enhanced - enhanced', 'The new features will enhance user experience.', 'Các tính năng mới sẽ tăng cường trải nghiệm người dùng.'),  
('era', '/ˈɪər.ə/', 'Danh từ', 'Noun', 'Kỷ nguyên', 'Một khoảng thời gian trong lịch sử với đặc điểm riêng biệt.', '-', 'We live in an era of technology.', 'Chúng ta sống trong một kỷ nguyên công nghệ.'),  
('guideline', '/ˈɡaɪd.laɪn/', 'Danh từ', 'Noun', 'Hướng dẫn', 'Một bộ quy tắc hoặc chỉ dẫn để làm theo.', '-', 'The guidelines help ensure safety in the workplace.', 'Các hướng dẫn giúp đảm bảo an toàn trong nơi làm việc.'),  
('incorporate', '/ɪnˈkɔːr.pə.reɪt/', 'Động từ', 'Verb', 'Kết hợp', 'Gồm có hoặc đưa cái gì đó vào một cái gì khác.', 'incorporate - incorporated - incorporated', 'You should incorporate feedback into your plan.', 'Bạn nên kết hợp phản hồi vào kế hoạch của mình.'),  
('interact', '/ˌɪn.təˈrækt/', 'Động từ', 'Verb', 'Tương tác', 'Có sự tác động lẫn nhau giữa hai hoặc nhiều đối tượng.', 'interact - interacted - interacted', 'Students are encouraged to interact with each other.', 'Học sinh được khuyến khích tương tác với nhau.'),  
('interval', '/ˈɪn.tə.vəl/', 'Danh từ', 'Noun', 'Khoảng thời gian', 'Một khoảng thời gian giữa hai sự kiện hoặc hoạt động.', '-', 'The show has a short interval for refreshments.', 'Chương trình có một khoảng thời gian ngắn cho nước uống.'),  
('mobile', '/ˈmoʊ.bəl/', 'Tính từ', 'Adjective', 'Di động', 'Có thể di chuyển hoặc thay đổi vị trí dễ dàng.', '-', 'Mobile technology has changed how we communicate.', 'Công nghệ di động đã thay đổi cách chúng ta giao tiếp.'),  
('modify', '/ˈmɒd.ɪ.faɪ/', 'Động từ', 'Verb', 'Sửa đổi', 'Thay đổi một phần của cái gì đó mà không thay đổi toàn bộ.', 'modify - modified - modified', 'You can modify the plan to fit your needs.', 'Bạn có thể sửa đổi kế hoạch để phù hợp với nhu cầu của mình.'),  
