('unsettle', '/ʌnˈsɛtəl/', 'Động từ', 'Verb', 'Làm bất an', 'Gây ra sự lo lắng hoặc khó chịu cho ai đó.', 'unsettle - unsettled - unsettled', 'The news unsettled many people.', 'Tin tức đã làm nhiều người bất an.'),  
('warp', '/wɔːrp/', 'Động từ', 'Verb', 'Biến dạng', 'Biến đổi hình dạng của một vật thông qua tác động của nhiệt độ hoặc độ ẩm.', 'warp - warped - warped', 'The wood began to warp due to moisture.', 'Gỗ bắt đầu bị biến dạng do độ ẩm.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES   
