INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES   
('certify', '/ˈsɜːrtɪfaɪ/', 'Động từ', 'Verb', 'Chứng nhận', 'Xác nhận tính chính xác hoặc hợp lệ của một cái gì đó.', 'certify - certified - certified', 'The official will certify the documents.', 'Cán bộ sẽ chứng nhận các tài liệu.'),  
('collaborate', '/kəˈlæbəreɪt/', 'Động từ', 'Verb', 'Hợp tác', 'Làm việc cùng nhau để hoàn thành một mục tiêu.', 'collaborate - collaborated - collaborated', 'They decided to collaborate on the project.', 'Họ đã quyết định hợp tác trong dự án.'),  
('compile', '/kəmˈpaɪl/', 'Động từ', 'Verb', 'Biên soạn', 'Thu thập thông tin từ nhiều nguồn để tạo thành một tài liệu hoàn chỉnh.', 'compile - compiled - compiled', 'She will compile the report by the end of the week.', 'Cô ấy sẽ biên soạn báo cáo trước cuối tuần.'),  
('counteract', '/ˌkaʊntərˈækt/', 'Động từ', 'Verb', 'Chống lại', 'Hành động để làm giảm hoặc làm mất tác dụng của điều gì đó.', 'counteract - counteracted - counteracted', 'They designed a plan to counteract the effects of pollution.', 'Họ đã thiết kế một kế hoạch để chống lại tác động của ô nhiễm.'),  
('curb', '/kɜːrb/', 'Động từ', 'Verb', 'Kiềm chế', 'Kiểm soát hoặc giảm thiểu điều gì đó.', 'curb - curbed - curbed', 'He tried to curb his bad habits.', 'Anh ấy cố gắng kiềm chế thói quen xấu của mình.'),  
('diagnose', '/ˈdaɪəɡnoʊz/', 'Động từ', 'Verb', 'Chẩn đoán', 'Xác định một vấn đề hoặc bệnh lý thông qua kiểm tra và nghiên cứu.', 'diagnose - diagnosed - diagnosed', 'The doctor will diagnose the illness after testing.', 'Bác sĩ sẽ chẩn đoán bệnh sau khi kiểm tra.'),  
('enact', '/ɪˈnækt/', 'Động từ', 'Verb', 'Thông qua', 'Thi hành một luật hoặc quy định.', 'enact - enacted - enacted', 'The government decided to enact new regulations.', 'Chính phủ đã quyết định thông qua các quy định mới.'),  
('federation', '/ˌfɛdəˈreɪʃən/', 'Danh từ', 'Noun', 'Liên bang', 'Một nhóm các tổ chức hoặc quốc gia kết hợp với nhau nhưng vẫn giữ quyền tự chủ.', '-', 'The federation was formed to promote cooperation.', 'Liên bang được thành lập để thúc đẩy hợp tác.'),  
('gross', '/ɡroʊs/', 'Tính từ', 'Adjective', 'Gộp', 'Tổng cộng, chưa trừ chi phí.', '-', 'The gross income was reported to be high.', 'Thu nhập gộp được báo cáo là cao.'),  
('humane', '/hjuːˈmeɪn/', 'Tính từ', 'Adjective', 'Nhân đạo', 'Thể hiện lòng từ bi và sự quan tâm đến người khác.', '-', 'The organization promotes humane treatment of animals.', 'Tổ chức thúc đẩy sự đối xử nhân đạo với động vật.'),  
('intolerable', '/ɪnˈtɑːlərəbl/', 'Tính từ', 'Adjective', 'Không thể chịu đựng', 'Nguyên nhân hoặc tình huống quá tồi tệ để một người có thể chấp nhận.', '-', 'The conditions in the factory were intolerable.', 'Điều kiện trong nhà máy là không thể chịu đựng nổi.'),  
('needy', '/ˈniːdi/', 'Tính từ', 'Adjective', 'Cần thiết', 'Cần sự hỗ trợ hoặc giúp đỡ.', '-', 'The charity helps needy families.', 'Tổ chức từ thiện giúp đỡ những gia đình cần thiết.'),  
('onset', '/ˈɑːnset/', 'Danh từ', 'Noun', 'Khởi đầu', 'Thời điểm xuất hiện hoặc bắt đầu một điều gì đó, thường là tiêu cực.', '-', 'The onset of winter brought heavy snowfall.', 'Khởi đầu của mùa đông mang đến tuyết rơi nặng.'),  
('pledge', '/plɛdʒ/', 'Danh từ', 'Noun', 'Lời hứa', 'Cam kết hoặc lời hứa chắc chắn thực hiện điều gì đó.', '-', 'He made a pledge to support the community.', 'Ông đã cam kết ủng hộ cộng đồng.'),  
('prohibit', '/prəˈhɪbɪt/', 'Động từ', 'Verb', 'Cấm', 'Ngăn cản hoặc không cho phép ai đó làm điều gì đó.', 'prohibit - prohibited - prohibited', 'The law prohibits smoking in public places.', 'Luật cấm việc hút thuốc ở những nơi công cộng.'),  
('rash', '/ræʃ/', 'Danh từ', 'Noun', 'Phát ban', 'Một tình trạng da gây mẩn đỏ hoặc ngứa.', '-', 'He developed a rash after using the new soap.', 'Anh ấy bị phát ban sau khi sử dụng xà phòng mới.'),  
('render', '/ˈrɛndər/', 'Động từ', 'Verb', 'Đưa ra', 'Cung cấp hoặc giải thích một cái gì đó.', 'render - rendered - rendered', 'The artist will render a beautiful painting.', 'Nghệ sĩ sẽ tạo ra một bức tranh đẹp.'),  
('smallpox', '/ˈsmɔːlpɒks/', 'Danh từ', 'Noun', 'Đậu mùa', 'Một bệnh truyền nhiễm nghiêm trọng gây ra mụn nhọt trên da.', '-', 'Smallpox was eradicated through vaccination.', 'Đậu mùa đã được tiêu diệt nhờ tiêm phòng.'),  
('transmit', '/trænzˈmɪt/', 'Động từ', 'Verb', 'Truyền', 'Gửi hoặc truyền tải thông tin từ nơi này đến nơi khác.', 'transmit - transmitted - transmitted', 'The radio transmits signals across long distances.', 'Đài phát thanh truyền tín hiệu qua khoảng cách xa.'),  
('vow', '/vaʊ/', 'Danh từ', 'Noun', 'Lời thề', 'Cam kết mạnh mẽ về một hành động hoặc sự việc.', '-', 'They made a vow to support each other.', 'Họ đã thực hiện lời thề sẽ ủng hộ lẫn nhau.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES   
('camouflage', '/ˈkæməˌflɑːʒ/', 'Danh từ', 'Noun', 'Ngụy trang', 'Một phương pháp làm cho một cái gì đó không thể nhìn thấy hoặc khó nhận biết.', '-', 'The soldier wore camouflage to blend in with the environment.', 'Người lính mặc trang phục ngụy trang để hòa nhập với môi trường.'),  
