INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('abide', '/əˈbaɪd/', 'Động từ', 'Verb', 'Tuân thủ', 'Chấp nhận và thực hiện các quy tắc hoặc quyết định.', '-', 'We must abide by the rules set by the committee.', 'Chúng ta phải tuân thủ các quy tắc do ủy ban đặt ra.'),  
('conversion', '/kənˈvɜːrʒən/', 'Danh từ', 'Noun', 'Sự chuyển đổi', 'Hành động hoặc quá trình chuyển đổi từ một trạng thái sang trạng thái khác.', '-', 'The conversion to renewable energy sources is essential.', 'Việc chuyển đổi sang nguồn năng lượng tái tạo là rất cần thiết.'),  
