INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('acrobat', '/ˈækrəˌbæt/', 'Danh từ', 'Noun', 'Nhà ảo thuật', 'Người biểu diễn các pha nhào lộn, thường trong một rạp xiếc.', '-', 'The acrobat performed amazing tricks high above the audience.', 'Nhà ảo thuật đã biểu diễn những trò tuyệt vời trên không trung trước mặt khán giả.'),  
