('janitor', '/ˈdʒænɪtər/', 'Danh từ', 'Noun', 'Người dọn dẹp', 'Người chịu trách nhiệm làm sạch và bảo trì một tòa nhà hoặc khu vực.', '-', 'The janitor keeps the school clean and tidy.', 'Người dọn dẹp giữ cho trường học sạch sẽ và ngăn nắp.'),  
('moist', '/mɔɪst/', 'Tính từ', 'Adjective', 'Ẩm ướt', 'Có một lượng nước nhẹ; không khô.', '-', 'The cake was delicious and perfectly moist.', 'Bánh ngọt thật ngon và ẩm vừa đủ.'),  
('nope', '/noʊp/', 'Thán từ', 'Interjection', 'Không', 'Một cách nói không; từ chối một cách bình dị.', '-', '“Are you coming to the party?” “Nope.”', '“Bạn có đến bữa tiệc không?” “Không.”'),  
('prod', '/prɒd/', 'Động từ', 'Verb', 'Thúc đẩy', 'Đâm hoặc chọc ai đó để thu hút sự chú ý của họ.', '-', 'He used a stick to prod the animal.', 'Anh ta đã dùng một cây gậy để thúc đẩy con vật.'),  
('ransom', '/ˈrænsəm/', 'Danh từ', 'Noun', 'Tiền chuộc', 'Số tiền yêu cầu để trả tự do cho người bị bắt cóc.', '-', 'The kidnappers demanded a ransom for her safe return.', 'Những kẻ bắt cóc đã yêu cầu tiền chuộc để cô trở về an toàn.'),  
('restrain', '/rɪˈstreɪn/', 'Động từ', 'Verb', 'Kiềm chế', 'Giữ hoặc kiểm soát một ai đó hoặc điều gì đó, không cho họ hành động theo cách họ muốn.', '-', 'The officers had to restrain the suspect.', 'Các sĩ quan đã phải kiềm chế nghi phạm.'),  
('saliva', '/səˈlaɪvə/', 'Danh từ', 'Noun', 'Nước bọt', 'Chất lỏng được sản xuất trong miệng để hỗ trợ trong việc tiêu hóa.', '-', 'Saliva helps break down food in the mouth.', 'Nước bọt giúp phá vỡ thức ăn trong miệng.'),  
('spit', '/spɪt/', 'Động từ', 'Verb', 'Nhổ', 'Phun ra nước bọt hoặc một chất lỏng nào đó từ miệng.', '-', 'He spat the gum out into the trash.', 'Anh ấy đã nhổ kẹo cao su vào thùng rác.'),  
('sprint', '/sprɪnt/', 'Động từ', 'Verb', 'Chạy nước rút', 'Chạy nhanh trong một khoảng thời gian ngắn.', '-', 'She had to sprint to catch the bus.', 'Cô ấy đã phải chạy nước rút để kịp xe buýt.'),  
('stunt', '/stʌnt/', 'Danh từ', 'Noun', 'Trò mạo hiểm', 'Một hành động hoặc nhiệm vụ nguy hiểm, thường là trong phim hoặc biểu diễn.', '-', 'He performed an incredible stunt in the movie.', 'Anh ấy đã thực hiện một màn mạo hiểm đáng kinh ngạc trong bộ phim.'),  
('tolerant', '/ˈtɒlərənt/', 'Tính từ', 'Adjective', 'Tha thứ', 'Có khả năng chấp nhận hoặc tôn trọng ý kiến, cảm xúc hoặc hành động khác biệt.', '-', 'We should be tolerant of different viewpoints.', 'Chúng ta nên tha thứ cho các quan điểm khác nhau.'),  
('vampire', '/ˈvæm.paɪər/', 'Danh từ', 'Noun', 'Ma cà rồng', 'Một sinh vật huyền bí thường được mô tả là hút máu người hoặc động vật khác.', '-', 'The story of the vampire has been popular in folklore.', 'Câu chuyện về ma cà rồng đã phổ biến trong văn hóa dân gian.'),  
('yawn', '/jɔn/', 'Động từ', 'Verb', 'Ngáp', 'Mở miệng rộng ra khi cảm thấy buồn ngủ hoặc mệt mỏi, thường kèm theo một cử động thở sâu.', '-', 'He couldn’t help but yawn during the long lecture.', 'Anh ấy không thể ngăn mình ngáp trong suốt bài giảng dài dòng.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('analogy', '/əˈnælədʒi/', 'Danh từ', 'Noun', 'Phép so sánh', 'Một sự so sánh giữa hai điều khác nhau để làm rõ ý tưởng hoặc khái niệm.', '-', 'She used an analogy to explain the situation more clearly.', 'Cô ấy đã sử dụng một phép so sánh để giải thích tình huống rõ ràng hơn.'),  
('ancestry', '/ˈæn.ses.tri/', 'Danh từ', 'Noun', 'Tổ tiên', 'Gốc gác hay nguồn gốc gia đình của một người.', '-', 'He researched his ancestry to learn more about his heritage.', 'Anh ấy đã nghiên cứu tổ tiên của mình để hiểu thêm về di sản của mình.'),  
('archer', '/ˈɑːr.tʃər/', 'Danh từ', 'Noun', 'Người bắn cung', 'Một người sử dụng cung và tên để bắn.', '-', 'The archer hit the target with precision.', 'Người bắn cung đã trúng mục tiêu một cách chính xác.'),  
('conspiracy', '/kənˈspɪrəsi/', 'Danh từ', 'Noun', 'Âm mưu', 'Một kế hoạch bí mật giữa hai hoặc nhiều người để thực hiện một hành động bất hợp pháp hoặc sai trái.', '-', 'The conspiracy was uncovered by the authorities.', 'Âm mưu đã bị phát hiện bởi các cơ quan chức năng.'),  
('deputy', '/ˈdɛpjʊti/', 'Danh từ', 'Noun', 'Phó', 'Người được ủy quyền làm nhiệm vụ thay cho người khác, thường là trong một vai trò chính phủ hoặc hành chính.', '-', 'The deputy will take over the duties during the mayor’s absence.', 'Phó sẽ đảm nhiệm các nhiệm vụ trong thời gian thị trưởng vắng mặt.'),  
('earl', '/ɜːrl/', 'Danh từ', 'Noun', 'Bá tước', 'Một tước vị quý tộc trong nền quân chủ, thường đứng thấp hơn công tước.', '-', 'The earl hosted a grand banquet in his castle.', 'Bá tước đã tổ chức một bữa tiệc lớn trong lâu đài của mình.'),  
('fragrant', '/ˈfreɪɡrənt/', 'Tính từ', 'Adjective', 'Thơm ngát', 'Có mùi dễ chịu hoặc thơm.', '-', 'The garden was filled with fragrant flowers.', 'Khu vườn tràn ngập những bông hoa thơm ngát.'),  
('funnel', '/ˈfʌn.əl/', 'Danh từ', 'Noun', 'Phễu', 'Một dụng cụ giúp dẫn hướng chất lỏng hoặc vật liệu dạng hạt vào một chỗ nhỏ hơn.', '-', 'She used a funnel to pour the liquid into the bottle.', 'Cô đã dùng một cái phễu để đổ chất lỏng vào chai.'),  
('hereditary', '/hərˈɛdɪtəˌri/', 'Tính từ', 'Adjective', 'Di truyền', 'Liên quan đến gen hoặc truyền từ cha mẹ sang con cái.', '-', 'He has a hereditary condition that affects his health.', 'Anh ấy có một tình trạng di truyền ảnh hưởng đến sức khỏe của mình.'),  
('hymn', '/hɪm/', 'Danh từ', 'Noun', 'Bài thánh ca', 'Một bài hát tôn vinh hoặc ca ngợi, thường được hát trong các lễ nghi tôn giáo.', '-', 'They sang a beautiful hymn during the ceremony.', 'Họ đã hát một bài thánh ca tuyệt đẹp trong buổi lễ.'),  
('invert', '/ɪnˈvɜːrt/', 'Động từ', 'Verb', 'Đảo ngược', 'Thay đổi vị trí của một cái gì đó để mặt bên này trở thành mặt bên khác.', '-', 'You can invert the image to see the negative.', 'Bạn có thể đảo ngược hình ảnh để xem âm bản.'),  
('prey', '/preɪ/', 'Danh từ', 'Noun', 'Con mồi', 'Một động vật mà một loài khác săn bắt để ăn.', '-', 'The lion stalks its prey silently.', 'Sư tử lén lút rình mồi.'),  
('procession', '/prəˈsɛʃən/', 'Danh từ', 'Noun', 'Đám rước', 'Một nhóm người di chuyển cùng nhau trong một hoạt động như lễ hội hoặc kỷ niệm.', '-', 'The parade was marked by a procession of colorful floats.', 'Lễ diễu hành được đánh dấu bởi một đám rước các xe hoa đầy màu sắc.'),  
('prophet', '/ˈprɒfɪt/', 'Danh từ', 'Noun', 'Nhà tiên tri', 'Một người được cho là nhận được thông điệp từ thần thánh để truyền đạt cho người khác.', '-', 'The prophet predicted a great change in the world.', 'Nhà tiên tri đã dự đoán một sự thay đổi lớn trong thế giới.'),  
('sarcastic', '/sɑrˈkæstɪk/', 'Tính từ', 'Adjective', 'Châm biếm', 'Sử dụng ngôn từ để thể hiện sự mỉa mai hoặc chế nhạo.', '-', 'His sarcastic remarks often go over people''s heads.', 'Những nhận xét châm biếm của anh ấy thường khó hiểu với mọi người.'),  
('seasoning', '/ˈsiː.zən.ɪŋ/', 'Danh từ', 'Noun', 'Gia vị', 'Các chất được thêm vào thực phẩm để cải thiện hương vị.', '-', 'Add salt as a seasoning for better flavor.', 'Thêm muối như một gia vị để có hương vị tốt hơn.'),  
('sodium', '/ˈsoʊdiəm/', 'Danh từ', 'Noun', 'Natri', 'Một nguyên tố hóa học, thường có trong muối và quan trọng cho sức khỏe.', '-', 'Sodium is essential for maintaining fluid balance in the body.', 'Natri rất cần thiết để duy trì sự cân bằng chất lỏng trong cơ thể.'),  
('tyranny', '/ˈtɪrəni/', 'Danh từ', 'Noun', 'Chế độ độc tài', 'Một hình thức chính quyền áp bức và độc tài, không tôn trọng quyền tự do của người dân.', '-', 'They sought to escape from the tyranny of the oppressor.', 'Họ đã tìm cách thoát khỏi chế độ độc tài của kẻ áp bức.'),  
('tyrant', '/ˈtaɪrənt/', 'Danh từ', 'Noun', 'Nhà độc tài', 'Một người cai trị hoặc lãnh đạo sử dụng quyền lực của mình một cách tàn bạo.', '-', 'The tyrant ruled with an iron fist, suppressing dissent.', 'Nhà độc tài đã cai trị bằng bàn tay sắt, đàn áp những người bất đồng.'),  
('vinegar', '/ˈvɪnɪɡər/', 'Danh từ', 'Noun', 'Giấm', 'Một chất lỏng chua được tạo ra bằng cách lên men rượu, thường được dùng để nêm nếm hoặc bảo quản thực phẩm.', '-', 'Vinegar is used in many salad dressings.', 'Giấm được sử dụng trong nhiều loại nước salad.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('analogous', '/əˈnæl.ə.ɡəs/', 'Tính từ', 'Adjective', 'Tương tự', 'Có sự tương đồng hoặc giống nhau về tính chất hoặc chức năng.', '-', 'The two situations are analogous in many ways.', 'Hai tình huống này tương tự nhau ở nhiều khía cạnh.'),  
('binoculars', '/bɪˈnɒk.jə.lərz/', 'Danh từ', 'Noun', 'Kính thiên văn', 'Một thiết bị quang học sử dụng để nhìn xa hơn, thường có hai ống kính.', '-', 'He brought binoculars to watch the birds.', 'Anh ấy đã mang theo kính thiên văn để quan sát chim.'),  
('bulk', '/bʌlk/', 'Danh từ', 'Noun', 'Khối lượng lớn', 'Kích thước lớn hoặc khối lượng của một vật hoặc sản phẩm.', '-', 'Buying in bulk often saves money.', 'Mua với khối lượng lớn thường tiết kiệm tiền.'),  
('comprise', '/kəmˈpraɪz/', 'Động từ', 'Verb', 'Bao gồm', 'Chứa hoặc hình thành nên một cái gì đó; cấu thành.', '-', 'The committee is comprised of ten members.', 'Ủy ban bao gồm mười thành viên.'),  
('depict', '/dɪˈpɪkt/', 'Động từ', 'Verb', 'Miêu tả', 'Vẽ hoặc mô tả một cái gì đó bằng hình ảnh hoặc ngôn từ.', '-', 'The painting depicts a beautiful landscape.', 'Bức tranh miêu tả một phong cảnh đẹp.'),  
('dual', '/ˈdjuː.əl/', 'Tính từ', 'Adjective', 'Đôi', 'Có hai phần, sử dụng hoặc thể hiện sự kết hợp của hai thứ.', '-', 'She has dual citizenship in Canada and France.', 'Cô ấy có hai quốc tịch ở Canada và Pháp.'),  
('Fahrenheit', '/ˈfær.ən.haɪt/', 'Danh từ', 'Noun', 'Độ Fahrenheit', 'Một thang đo nhiệt độ, trong đó điểm đóng băng của nước là 32 độ và sôi là 212 độ.', '-', 'The temperature today is 75 degrees Fahrenheit.', 'Nhiệt độ hôm nay là 75 độ Fahrenheit.'),  
('fulfill', '/fʊlˈfɪl/', 'Động từ', 'Verb', 'Đáp ứng', 'Thực hiện hoặc hoàn thành một điều gì đó, thường là một yêu cầu hoặc cam kết.', '-', 'They hope to fulfill their dream of traveling the world.', 'Họ hy vọng sẽ đáp ứng ước mơ đi du lịch khắp thế giới.'),  
('grove', '/ɡroʊv/', 'Danh từ', 'Noun', 'Khu rừng nhỏ', 'Một khu vực nhỏ có nhiều cây cối, thường là cây ăn quả hoặc cây xanh.', '-', 'We walked through the grove of olive trees.', 'Chúng tôi đã đi bộ qua khu rừng cây ô liu.'),  
('ore', '/ɔːr/', 'Danh từ', 'Noun', 'Quặng', 'Một loại đá chứa khoáng sản quý có thể được khai thác.', '-', 'Iron ore is processed to make steel.', 'Quặng sắt được chế biến để làm thép.'),  
('outback', '/ˈaʊt.bæk/', 'Danh từ', 'Noun', 'Vùng hoang dã', 'Khu vực hẻo lánh và ít dân cư, thường được dùng để chỉ khu vực nông thôn của Australia.', '-', 'The outback is known for its vast landscapes.', 'Vùng hoang dã nổi tiếng với những cảnh quan rộng lớn.'),  
('outweigh', '/ˌaʊtˈweɪ/', 'Động từ', 'Verb', 'Vượt trội hơn', 'Trọng lượng hoặc giá trị của cái này nhiều hơn cái khác.', '-', 'The benefits of the program outweigh the costs.', 'Lợi ích của chương trình vượt trội hơn chi phí.'),  
('paradox', '/ˈpær.ə.dɒks/', 'Danh từ', 'Noun', 'Nghịch lý', 'Một tuyên bố hoặc sự kiện dường như mâu thuẫn nhưng lại có thể đúng.', '-', 'It’s a paradox that the more we know, the more we realize we don’t know.', 'Đó là một nghịch lý rằng càng biết nhiều, chúng ta càng nhận ra mình không biết gì.'),  
('pier', '/pɪr/', 'Danh từ', 'Noun', 'Cầu tàu', 'Một cấu trúc xây dựng ra biển, thường dùng để neo đậu tàu thuyền hoặc để đi dạo.', '-', 'We walked along the pier to enjoy the sunset.', 'Chúng tôi đã đi dạo dọc cầu tàu để thưởng thức hoàng hôn.'),  
('shortcut', '/ˈʃɔːr.kʌt/', 'Danh từ', 'Noun', 'Đường tắt', 'Một lối đi hoặc phương pháp để tiết kiệm thời gian hoặc làm cho điều gì đó trở nên dễ dàng hơn.', '-', 'Taking the shortcut saved us a lot of time.', 'Đi đường tắt đã giúp chúng tôi tiết kiệm rất nhiều thời gian.'),  
('tariff', '/ˈtær.ɪf/', 'Danh từ', 'Noun', 'Thuế xuất nhập khẩu', 'Một loại thuế được áp dụng cho hàng hóa được nhập khẩu hoặc xuất khẩu.', '-', 'The government raised tariffs on imported goods.', 'Chính phủ đã tăng thuế xuất nhập khẩu đối với hàng hóa nhập khẩu.'),  
('thermometer', '/θərˈmɒmɪtər/', 'Danh từ', 'Noun', 'Nhiệt kế', 'Một dụng cụ dùng để đo nhiệt độ.', '-', 'The thermometer showed a drop in temperature.', 'Nhiệt kế cho thấy nhiệt độ giảm xuống.'),  
('tilt', '/tɪlt/', 'Động từ', 'Verb', 'Nghiêng', 'Làm cho một cái gì đó đứng nghiêng hoặc lệch đi.', '-', 'He tilted his head to listen more closely.', 'Anh ấy đã nghiêng đầu để lắng nghe kỹ hơn.'),  
('vice versa', '/ˌvaɪs ˈvɜːrsə/', 'Cụm từ', 'Phrase', 'Ngược lại', 'Sử dụng để chỉ rằng hai điều có thể trao đổi cho nhau trong một ngữ cảnh nhất định.', '-', 'She likes him, and vice versa.', 'Cô ấy thích anh ấy, và ngược lại.'),  
('whereabouts', '/ˈwɛərəˌbaʊts/', 'Danh từ', 'Noun', 'Nơi ở', 'Vị trí hoặc địa điểm của một người hoặc vật.', '-', 'The police are trying to determine his whereabouts.', 'Cảnh sát đang cố gắng xác định nơi ở của anh ấy.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('amber', '/ˈæmbər/', 'Danh từ', 'Noun', 'Hổ phách', 'Một loại nhựa hóa thạch có màu vàng hoặc nâu, thường được dùng làm trang sức.', '-', 'She wore a beautiful amber necklace.', 'Cô ấy đeo một chiếc vòng cổ hổ phách đẹp.'),  
('charcoal', '/ˈtʃɑːrˌkoʊl/', 'Danh từ', 'Noun', 'Than hoạt tính', 'Một loại vật liệu được sản xuất từ carbon, thường dùng để nướng thịt hoặc trong hội họa.', '-', 'They used charcoal to grill the meat.', 'Họ đã sử dụng than hoạt tính để nướng thịt.'),  
('columnist', '/ˈkɒləm.nɪst/', 'Danh từ', 'Noun', 'Nhà báo chuyên mục', 'Một nhà báo viết bài cho một chuyên mục định kỳ hoặc một trang báo.', '-', 'The columnist writes about social issues every week.', 'Nhà báo chuyên mục viết về các vấn đề xã hội mỗi tuần.'),  
('courteous', '/ˈkɜːrtiəs/', 'Tính từ', 'Adjective', 'Lịch sự', 'Thể hiện sự tôn trọng và lịch thiệp đối với người khác.', '-', 'The courteous staff made our stay very pleasant.', 'Nhân viên lịch sự đã làm cho kỳ nghỉ của chúng tôi rất dễ chịu.'),  
('credentials', '/krɪˈdɛnʃəlz/', 'Danh từ', 'Noun', 'Chứng chỉ', 'Các tài liệu chứng minh khả năng, kinh nghiệm hoặc trình độ của một người.', '-', 'She has impressive credentials for the job.', 'Cô ấy có chứng chỉ ấn tượng cho công việc này.'),  
('crickets', '/ˈkrɪkɪts/', 'Danh từ', 'Noun', 'Dế', 'Một loại côn trùng có tiếng kêu đặc trưng, thường nghe thấy vào ban đêm.', '-', 'The sound of crickets filled the evening air.', 'Âm thanh của những con dế lấp đầy không khí buổi tối.'),  
('delta', '/ˈdɛltə/', 'Danh từ', 'Noun', 'Hạ lưu', 'Nơi gặp nhau của một con sông với biển, thường tạo thành một vùng đất hình tam giác.', '-', 'The Mississippi River forms a large delta at its mouth.', 'Sông Mississippi tạo thành một hạ lưu lớn tại cửa của nó.'),  
('detergent', '/dɪˈtɜrdʒənt/', 'Danh từ', 'Noun', 'Chất tẩy rửa', 'Một loại hóa chất dùng để tẩy rửa hoặc làm sạch đồ vật, thường là quần áo.', '-', 'She used a strong detergent to wash the clothes.', 'Cô ấy đã sử dụng một chất tẩy rửa mạnh để giặt quần áo.'),  
('euphemism', '/ˈjuː.fə.mɪ.zəm/', 'Danh từ', 'Noun', 'Lời nói giảm nhẹ', 'Một thuật ngữ dùng để diễn tả điều gì đó đáng lo ngại hoặc khó chịu một cách nhẹ nhàng hơn.', '-', 'He used a euphemism to avoid saying "die."', 'Anh ấy đã sử dụng lời nói giảm nhẹ để tránh nói "chết".'),  
('expire', '/ɪkˈspaɪr/', 'Động từ', 'Verb', 'Hết hạn', 'Trở nên không còn hiệu lực hoặc không còn sử dụng được.', '-', 'The contract will expire next month.', 'Hợp đồng sẽ hết hạn vào tháng tới.'),  
('granite', '/ˈɡrænɪt/', 'Danh từ', 'Noun', 'Đá hoa cương', 'Một loại đá cứng được hình thành từ magma, thường dùng trong xây dựng.', '-', 'The countertop is made of polished granite.', 'Bàn bếp được làm từ đá hoa cương đánh bóng.'),  
('gravel', '/ˈɡrævəl/', 'Danh từ', 'Noun', 'Sỏi', 'Các hạt đá nhỏ hơn đá cuội, thường dùng để xây dựng đường hoặc lối đi.', '-', 'The path was covered with gravel.', 'Con đường được phủ bằng sỏi.'),  
('haunt', '/hɔːnt/', 'Động từ', 'Verb', 'Ám ảnh', 'Thường xuyên đến thăm một địa điểm hoặc một ý nghĩ nào đó, thường là tiêu cực.', '-', 'The ghost is said to haunt the old mansion.', 'Hồn ma được cho là ám ảnh ngôi biệt thự cũ.'),  
('liberal', '/ˈlɪbərəl/', 'Tính từ', 'Adjective', 'Tự do', 'Có tư tưởng thoáng, thường ủng hộ quyền tự do cá nhân và bình đẳng.', '-', 'The liberal government supports various social reforms.', 'Chính phủ tự do ủng hộ nhiều cải cách xã hội khác nhau.'),  
('maze', '/meɪz/', 'Danh từ', 'Noun', 'Mê cung', 'Một cấu trúc phức tạp gồm nhiều lối đi, thường để thử thách khả năng tìm đường.', '-', 'We got lost in the maze during the fair.', 'Chúng tôi đã lạc trong mê cung trong suốt hội chợ.'),  
('moss', '/mɔs/', 'Danh từ', 'Noun', 'Rêu', 'Một loại thực vật nhỏ, xanh, thường phát triển ở nơi ẩm thấp.', '-', 'Moss grows in damp, shady areas of the forest.', 'Rêu phát triển ở những khu vực ẩm ướt và râm mát của rừng.'),  
('peck', '/pɛk/', 'Động từ', 'Verb', 'Nhón', 'Để chạm hoặc mổ nhẹ vào một cái gì đó, thường được thực hiện bởi chim.', '-', 'The bird began to peck at the seeds on the ground.', 'Con chim bắt đầu nhón hạt giống trên mặt đất.'),  
('pebble', '/ˈpɛbəl/', 'Danh từ', 'Noun', 'Sỏi nhỏ', 'Một viên đá nhỏ, tròn và nhẵn, thường được tìm thấy trong tự nhiên.', '-', 'The beach is covered with smooth pebbles.', 'Bãi biển được phủ bằng những viên sỏi mịn.'),  
('reservoir', '/ˈrɛzərˌvɔr/', 'Danh từ', 'Noun', 'Hồ chứa nước', 'Một vùng nước lớn được tạo ra để lưu trữ nước uống hoặc cho tưới tiêu.', '-', 'The city relies on the reservoir for its drinking water.', 'Thành phố phụ thuộc vào hồ chứa nước cho nguồn nước uống của mình.'),  
('streak', '/striːk/', 'Danh từ', 'Noun', 'Vệt', 'Một đường dài hẹp của màu sắc hoặc ánh sáng khác biệt.', '-', 'A streak of sunlight broke through the clouds.', 'Một vệt ánh sáng mặt trời xuyên qua những đám mây.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('adjoining', '/əˈdʒɔɪnɪŋ/', 'Tính từ', 'Adjective', 'Kế bên', 'Nằm bên cạnh hoặc liền kề với một cái gì đó.', '-', 'The adjoining rooms make it easy for families to stay together.', 'Các phòng kế bên giúp cho các gia đình dễ dàng ở gần nhau.'),  
('allege', '/əˈlɛdʒ/', 'Động từ', 'Verb', 'Cho rằng', 'Tuyên bố điều gì đó mà không có bằng chứng chắc chắn.', '-', 'The report alleges that the company committed fraud.', 'Báo cáo cho rằng công ty đã gian lận.'),  
('arch', '/ɑːrtʃ/', 'Danh từ', 'Noun', 'Cổng vòm', 'Một cấu trúc có hình dạng cong, thường được sử dụng trong kiến trúc.', '-', 'The archway led to the beautiful garden.', 'Cổng vòm dẫn vào khu vườn đẹp.'),  
('assemble', '/əˈsɛmbl/', 'Động từ', 'Verb', 'Tập hợp', 'Thu thập và lắp ráp các phần lại với nhau.', '-', 'They will assemble the furniture this weekend.', 'Họ sẽ lắp ráp đồ nội thất vào cuối tuần này.'),  
('casualty', '/ˈkæʒuəlti/', 'Danh từ', 'Noun', 'Thương vong', 'Một người bị thương hoặc chết trong một tai nạn hoặc cuộc xung đột.', '-', 'The accident resulted in several casualties.', 'Tai nạn đã dẫn đến nhiều thương vong.'),  
('erect', '/ɪˈrɛkt/', 'Động từ', 'Verb', 'Xây dựng', 'Đặt hoặc cài đặt một cấu trúc theo chiều thẳng đứng.', '-', 'They will erect a new building downtown.', 'Họ sẽ xây dựng một tòa nhà mới ở trung tâm thành phố.'),  
('foul', '/faʊl/', 'Tính từ', 'Adjective', 'Hôi thối', 'Có mùi khó chịu hoặc không tốt; có hành vi sai trái hoặc không đúng.', '-', 'He complained about the foul odor in the room.', 'Anh ấy phàn nàn về mùi hôi thối trong phòng.'),  
('hectare', '/ˈhɛk.tɛr/', 'Danh từ', 'Noun', 'Héc ta', 'Một đơn vị đo diện tích bằng 10.000 mét vuông.', '-', 'The farm covers an area of 50 hectares.', 'Trang trại có diện tích 50 héc ta.'),  
('heighten', '/ˈhaɪtən/', 'Động từ', 'Verb', 'Tăng cường', 'Tăng cường, làm cho cái gì đó trở nên mạnh mẽ hơn.', '-', 'The new policy will heighten security measures.', 'Chính sách mới sẽ tăng cường các biện pháp an ninh.'),  
('hospitality', '/ˌhɒspɪˈtælɪti/', 'Danh từ', 'Noun', 'Lòng hiếu khách', 'Hành động hoặc trạng thái chào đón khách, thường đi kèm với sự thân thiện và phục vụ.', '-', 'Their hospitality made us feel at home.', 'Lòng hiếu khách của họ đã khiến chúng tôi cảm thấy như ở nhà.'),  
('mansion', '/ˈmænʃən/', 'Danh từ', 'Noun', 'Biệt thự', 'Một ngôi nhà lớn và sang trọng, thường dành cho người giàu có.', '-', 'They live in a beautiful mansion by the lake.', 'Họ sống trong một biệt thự đẹp bên hồ.'),  
('outnumber', '/aʊtˈnʌmbər/', 'Động từ', 'Verb', 'Vượt quá số lượng', 'Có nhiều hơn số lượng của một nhóm khác.', '-', 'The students outnumber the teachers in the school.', 'Học sinh đông hơn giáo viên ở trường.'),  
('overjoyed', '/ˌoʊvərˈdʒɔɪd/', 'Tính từ', 'Adjective', 'Hạnh phúc vô bờ', 'Cảm thấy cực kỳ vui mừng hoặc hạnh phúc.', '-', 'She was overjoyed to receive the good news.', 'Cô ấy rất hạnh phúc khi nhận được tin tốt.'),  
('pasture', '/ˈpæstʃər/', 'Danh từ', 'Noun', 'Đồng cỏ', 'Một khu vực đất dành để chăn thả gia súc, thường phủ đầy cỏ.', '-', 'The cows grazed peacefully in the pasture.', 'Những con bò gặm cỏ một cách yên bình trên đồng cỏ.'),  
('petition', '/pəˈtɪʃən/', 'Danh từ', 'Noun', 'Đơn kiến nghị', 'Một yêu cầu chính thức được gửi đến một tổ chức hoặc cơ quan để thay đổi điều gì đó.', '-', 'They created a petition to save the park.', 'Họ đã tạo ra một đơn kiến nghị để cứu vườn hoa.'),  
('renovate', '/ˈrɛnəˌveɪt/', 'Động từ', 'Verb', 'Cải tạo', 'Cập nhật hoặc làm mới một cái gì đó, thường là một tòa nhà hoặc không gian sống.', '-', 'They plan to renovate the old theater next year.', 'Họ dự định cải tạo nhà hát cũ vào năm tới.'),  
('revise', '/rɪˈvaɪz/', 'Động từ', 'Verb', 'Sửa đổi', 'Xem xét và chỉnh sửa để nâng cao chất lượng hoặc tính chính xác.', '-', 'She needs to revise her essay before submission.', 'Cô ấy cần sửa đổi bài tiểu luận của mình trước khi nộp.'),  
('slab', '/slæb/', 'Danh từ', 'Noun', 'Tấm', 'Một mảnh vật liệu phẳng và dày, như bê tông hoặc đá.', '-', 'The workers placed a large slab of concrete on the ground.', 'Các công nhân đã đặt một tấm bê tông lớn trên mặt đất.'),  
('terrace', '/ˈtɛr.əs/', 'Danh từ', 'Noun', 'Hiên', 'Một khu vực phẳng ngoài trời, thường là ở tầng trên của một tòa nhà.', '-', 'They enjoyed dinner on the terrace overlooking the garden.', 'Họ đã thưởng thức bữa tối trên hiên nhìn ra vườn.'),  
('turf', '/tɜrf/', 'Danh từ', 'Noun', 'Giấy cỏ', 'Một lớp đất hoặc cỏ được gắn chặt lại, thường dùng để tạo sân cỏ hoặc tô điểm cho vườn.', '-', 'The garden was covered in lush green turf.', 'Khu vườn được phủ một lớp cỏ xanh tươi.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('amid', '/əˈmɪd/', 'Giới từ', 'Preposition', 'Giữa', 'Ở giữa hoặc trong một nơi nào đó.', '-', 'The ceremony took place amid great fanfare.', 'Buổi lễ diễn ra giữa sự hoành tráng lớn lao.'),  
('backstage', '/ˈbækˌsteɪdʒ/', 'Danh từ', 'Noun', 'Hậu trường', 'Khu vực phía sau sân khấu nơi các diễn viên và thiết bị chuẩn bị trước khi biểu diễn.', '-', 'The actors waited backstage before their performance.', 'Các diễn viên chờ đợi ở hậu trường trước khi biểu diễn.'),  
('billionaire', '/ˌbɪl.jəˈneər/', 'Danh từ', 'Noun', 'Tỉ phú', 'Một người sở hữu tài sản trị giá một tỷ đô la hoặc nhiều hơn.', '-', 'The billionaire donated a large sum to charity.', 'Người tỉ phú đã quyên góp một số tiền lớn cho tổ chức từ thiện.'),  
('brute', '/bruːt/', 'Danh từ', 'Noun', 'Thú vật', 'Người hoặc vật mạnh mẽ, thường có hành động thô bạo hoặc khắc nghiệt.', '-', 'He acted like a brute during the argument.', 'Anh ấy đã hành động như một thú vật trong cuộc tranh cãi.'),  
('clumsy', '/ˈklʌm.zi/', 'Tính từ', 'Adjective', 'Vụng về', 'Thiếu sự khéo léo, thường làm việc một cách vụng về hoặc kém hiệu quả.', '-', 'The clumsy waiter spilled the drink all over the table.', 'Người phục vụ vụng về đã làm đổ đồ uống ra khắp bàn.'),  
('collide', '/kəˈlaɪd/', 'Động từ', 'Verb', 'Va chạm', 'Hai vật hoặc nhiều vật đập vào nhau một cách mạnh mẽ.', '-', 'The two cars collided at the intersection.', 'Hai chiếc xe đã va chạm tại ngã tư.'),  
('culprit', '/ˈkʌlprɪt/', 'Danh từ', 'Noun', 'Thủ phạm', 'Người bị buộc tội hoặc được cho là đã làm điều sai trái.', '-', 'The police caught the culprit after the robbery.', 'Cảnh sát đã bắt giữ thủ phạm sau vụ cướp.'),  
('evacuate', '/ɪˈvæk.ju.eɪt/', 'Động từ', 'Verb', 'Rút khỏi', 'Rời khỏi một khu vực để đảm bảo an toàn, thường trong trường hợp khẩn cấp.', '-', 'Residents had to evacuate the area due to the flood.', 'Cư dân đã phải rút khỏi khu vực do lũ lụt.'),  
('flammable', '/ˈflæm.ə.bəl/', 'Tính từ', 'Adjective', 'Dễ cháy', 'Có khả năng bắt lửa hoặc bùng nổ dễ dàng.', '-', 'Gasoline is a highly flammable substance.', 'Xăng là một chất dễ cháy cao.'),  
('mob', '/mɒb/', 'Danh từ', 'Noun', 'Đám đông', 'Một nhóm lớn người thường có hành vi mãnh liệt hoặc hỗn loạn.', '-', 'A mob gathered to protest the decision.', 'Một đám đông đã tập hợp để phản đối quyết định.'),  
('premature', '/ˈpriː.mə.tʃʊr/', 'Tính từ', 'Adjective', 'Non yếu', 'Xảy ra quá sớm; không trưởng thành hoặc phát triển đầy đủ.', '-', 'The baby was born premature and needed special care.', 'Đứa bé sinh non và cần sự chăm sóc đặc biệt.'),  
('resent', '/rɪˈzɛnt/', 'Động từ', 'Verb', 'Oán giận', 'Cảm thấy tức giận hoặc không hài lòng vì điều gì đó không công bằng.', '-', 'She resented having to work overtime without pay.', 'Cô ấy oán giận vì phải làm thêm giờ mà không được trả lương.'),  
('satire', '/ˈsæt.aɪər/', 'Danh từ', 'Noun', 'Châm biếm', 'Một hình thức nghệ thuật dùng để chỉ trích hoặc mỉa mai, thường là các vấn đề xã hội hoặc chính trị.', '-', 'The film is a satire of modern consumer culture.', 'Bộ phim là một tác phẩm châm biếm về văn hóa tiêu dùng hiện đại.'),  
('scrutiny', '/ˈskruː.tɪ.ni/', 'Danh từ', 'Noun', 'Sự xem xét kỹ lưỡng', 'Sự kiểm tra chặt chẽ hoặc phân tích một cách cẩn thận.', '-', 'The proposal will be subject to scrutiny by the board.', 'Đề xuất sẽ được xem xét kỹ lưỡng bởi ban giám đốc.'),  
('segregate', '/ˈsɛɡrɪɡeɪt/', 'Động từ', 'Verb', 'Phân chia', 'Tách biệt hoặc chia tách nhóm người hoặc vật.', '-', 'The policy segregates students based on their abilities.', 'Chính sách phân chia học sinh theo khả năng của họ.'),  
('testify', '/ˈtɛstəˌfaɪ/', 'Động từ', 'Verb', 'Làm chứng', 'Phát biểu hoặc đưa ra thông tin dưới hình thức lời khai trong một phiên tòa.', '-', 'She will testify as a witness in the trial.', 'Cô ấy sẽ làm chứng như một nhân chứng trong phiên tòa.'),  
('tumult', '/ˈtjuː.mʌlt/', 'Danh từ', 'Noun', 'Sự ồn ào', 'Một trạng thái ồn ào, hỗn loạn hoặc náo động.', '-', 'The tumult in the streets lasted for hours.', 'Sự ồn ào trên đường phố kéo dài hàng giờ.'),  
('underestimate', '/ˌʌn.dɚˈɛstɪ.meɪt/', 'Động từ', 'Verb', 'Đánh giá thấp', 'Đánh giá giá trị, số lượng hoặc tầm quan trọng của một điều gì đó thấp hơn thực tế.', '-', 'Never underestimate the power of teamwork.', 'Đừng bao giờ đánh giá thấp sức mạnh của sự hợp tác.'),  
('uproar', '/ˈʌp.rɔːr/', 'Danh từ', 'Noun', 'Sự náo loạn', 'Một tình huống gây ra sự bất bình hoặc phẫn nộ lớn.', '-', 'The announcement caused an uproar among the fans.', 'Thông báo đã gây ra sự náo loạn trong số các fan.');
INSERT INTO public.vocabulary (word, phonetic, word_type, word_type_en, meaning_vn, meaning_explanation_vn, verb_forms, example, example_meaning) VALUES  
('accelerate', '/əkˈsɛləreɪt/', 'Động từ', 'Verb', 'Tăng tốc', 'Làm cho một cái gì đó xảy ra nhanh hơn hoặc tiến triển nhanh hơn.', '-', 'The car began to accelerate as it went downhill.', 'Chiếc xe bắt đầu tăng tốc khi xuống dốc.'),  
('anew', '/əˈnjuː/', 'Phó từ', 'Adverb', 'Lại lần nữa', 'Bắt đầu hoặc làm lại ở một thời điểm hoặc trong một ngữ cảnh mới.', '-', 'She decided to start her career anew in a different city.', 'Cô ấy quyết định bắt đầu sự nghiệp của mình lại lần nữa ở một thành phố khác.'),  
('defect', '/ˈdiːfɛkt/', 'Danh từ', 'Noun', 'Khuyết điểm', 'Một vấn đề hoặc thiếu sót trong một cái gì đó.', '-', 'The product was recalled due to a defect in its design.', 'Sản phẩm đã bị thu hồi do có khuyết điểm trong thiết kế của nó.'),  
